import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Checkout() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const festa = state?.festa;

  if (!festa) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-900 to-blue-900 text-white p-6">
        <div className="bg-black bg-opacity-30 p-8 rounded-xl backdrop-blur-sm border border-white border-opacity-10 shadow-2xl">
          <h2 className="text-2xl font-bold mb-4">Ops!</h2>
          <p className="text-lg">Festa não selecionada. Volte e escolha uma festa para comprar ingressos.</p>
          <button 
            onClick={() => navigate(-1)} 
            className="mt-6 px-6 py-2 bg-gradient-to-r from-purple-600 to-blue-600 rounded-full font-medium transition-all hover:from-purple-500 hover:to-blue-500 hover:shadow-lg"
          >
            Voltar para seleção
          </button>
        </div>
      </div>
    );
  }

  const categorias = festa.categorias;
  const [quantidades, setQuantidades] = useState(
    Object.fromEntries(Object.keys(categorias).map(cat => [cat, 0]))
  );

  const handleChange = (cat, value) => {
    const v = Math.max(0, Math.min(5, Number(value)));
    setQuantidades(prev => ({ ...prev, [cat]: v }));
  };

  const total = Object.entries(quantidades)
    .reduce((sum, [cat, qty]) => sum + qty * categorias[cat], 0);

  const handleFinalize = () => {
    navigate('/confirmar', { state: { festaName: festa.nome, items: quantidades, total } });
  };

  const handleQuantityChange = (cat, increment) => {
    const currentValue = quantidades[cat];
    const newValue = increment 
      ? Math.min(5, currentValue + 1) 
      : Math.max(0, currentValue - 1);
    setQuantidades(prev => ({ ...prev, [cat]: newValue }));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-indigo-900 to-blue-900 text-white p-6">
      <div className="max-w-md mx-auto">
        <button 
          onClick={() => navigate(-1)} 
          className="mb-8 flex items-center text-lg hover:underline transition-colors text-purple-300 hover:text-white"
        >
          <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
          </svg>
          Voltar
        </button>
        
        <div className="bg-black bg-opacity-30 rounded-2xl p-8 backdrop-blur-sm border border-white border-opacity-10 shadow-2xl">
          <div className="mb-8">
            <h2 className="text-lg uppercase text-purple-300 tracking-wider font-medium">Seleção de Ingressos</h2>
            <h1 className="text-3xl font-bold">{festa.nome}</h1>
          </div>
          
          <div className="space-y-6 mb-8">
            {Object.entries(categorias).map(([cat, preco]) => (
              <div key={cat} className="bg-white bg-opacity-5 rounded-xl p-4 transition-all hover:bg-opacity-10">
                <div className="flex justify-between items-center">
                  <div>
                    <p className="font-medium text-lg">{cat}</p>
                    <p className="text-purple-300 font-bold">R$ {preco.toFixed(2)}</p>
                  </div>
                  
                  <div className="flex items-center">
                    <button 
                      onClick={() => handleQuantityChange(cat, false)}
                      className="w-8 h-8 flex items-center justify-center rounded-full bg-purple-800 text-white hover:bg-purple-700 transition-colors"
                    >
                      <span className="text-lg font-bold pb-0.5">-</span>
                    </button>
                    
                    <input
                      type="number"
                      min={0}
                      max={5}
                      value={quantidades[cat]}
                      onChange={e => handleChange(cat, e.target.value)}
                      className="w-12 mx-2 bg-transparent text-white text-center text-lg font-bold"
                    />
                    
                    <button 
                      onClick={() => handleQuantityChange(cat, true)}
                      className="w-8 h-8 flex items-center justify-center rounded-full bg-purple-800 text-white hover:bg-purple-700 transition-colors"
                    >
                      <span className="text-lg font-bold pb-0.5">+</span>
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
          
          <div className="mt-6 border-t border-white border-opacity-10 pt-6">
            <div className="flex justify-between items-center mb-6">
              <span className="text-lg">Total</span>
              <span className="text-2xl font-bold text-purple-300">R$ {total.toFixed(2)}</span>
            </div>
            
            <button
              onClick={handleFinalize}
              disabled={total === 0}
              className="w-full py-3 bg-gradient-to-r from-purple-600 to-blue-600 text-white font-bold rounded-xl transition-all hover:from-purple-500 hover:to-blue-500 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg hover:shadow-xl text-lg"
            >
              {total === 0 ? 'Selecione pelo menos um ingresso' : 'Finalizar Compra'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
