// src/ConfirmPage.js
import { useSearchParams, useNavigate } from 'react-router-dom';
import { useState } from 'react';

export default function ConfirmPage() {
  const [params] = useSearchParams();
  const navigate = useNavigate();
  const [status, setStatus] = useState(null);

  const ticket = params.get('ingresso');
  const price = params.get('valor');
  const role = params.get('role');
  const source = params.get('source');

  const actionText = role === 'comprador' ? 'vender' : 'comprar';

  const handleConfirm = () => {
    setStatus('confirmed');
    setTimeout(() => {
      // Nova lógica: se o link foi criado pelo vendedor (source === 'vendedor'), redireciona para pagamento
      if (source === 'vendedor') {
        navigate(`/pagamento?valor=${encodeURIComponent(price)}`);
      } else {
        navigate('/aguardando');
      }
    }, 2000);
  };

  const handleReject = () => {
    setStatus('rejected');
    setTimeout(() => navigate('/cancelado'), 2000);
  };

  return (
    <main className="min-h-screen bg-gray-900 text-white font-sans">
      <header className="w-full p-6 shadow-md bg-gray-950 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src="/logo.png.jpg" alt="logo" className="h-10 w-10 rounded-xl" />
          <h1 className="text-2xl font-bold tracking-tight">Safeswap</h1>
        </div>
        <p className="text-sm text-gray-400">Seguro e rápido</p>
      </header>

      <section className="flex flex-col items-center justify-center text-center px-6 py-16">
        {!ticket || !price || !role ? (
          <>
            <h1 className="text-3xl font-bold mb-2">Link inválido</h1>
            <p className="text-gray-400">Faltam informações na URL.</p>
          </>
        ) : status === 'confirmed' ? (
          <>
            <h1 className="text-3xl font-bold mb-2">Operação confirmada!</h1>
            <p className="text-gray-400">Redirecionando...</p>
          </>
        ) : status === 'rejected' ? (
          <>
            <h1 className="text-3xl font-bold mb-2">Operação recusada</h1>
            <p className="text-gray-400">Redirecionando para a mensagem de cancelamento...</p>
          </>
        ) : (
          <>
            <h1 className="text-3xl font-bold mb-4">Confirmação de operação</h1>
            <p className="text-lg mb-1">Você está prestes a <strong>{actionText}</strong> o ingresso:</p>
            <p className="text-2xl font-semibold">{ticket}</p>
            <p className="text-lg text-gray-400 mb-6">por R$ {price}</p>

            <div className="flex gap-6">
              <button
                onClick={handleConfirm}
                className="bg-green-500 hover:bg-green-600 text-white px-6 py-2 rounded-lg shadow"
              >
                ✅ Confirmar
              </button>
              <button
                onClick={handleReject}
                className="bg-red-500 hover:bg-red-600 text-white px-6 py-2 rounded-lg shadow"
              >
                ❌ Cancelar
              </button>
            </div>
          </>
        )}
      </section>
    </main>
  );
}
