import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // ✅ novo import
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const navigate = useNavigate(); // ✅ instância do navigate

  useEffect(() => {
    const ordenadas = festasJson.sort(
      (a, b) => new Date(a.data) - new Date(b.data)
    );
    setFestas(ordenadas);
  }, []);

  const handleDateChange = (date) => {
    setDataSelecionada(date);
  };

  const festasFiltradas = dataSelecionada
    ? festas.filter(
        (f) => f.data === dataSelecionada.toISOString().split('T')[0]
      )
    : festas;

  const tileClassName = ({ date }) => {
    const dateStr = date.toISOString().split('T')[0];
    const existeFesta = festas.some((f) => f.data === dateStr);
    return existeFesta ? 'bg-green-500 text-white rounded-full' : null;
  };

  return (
    <div className="relative max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-md">

      {/* Botão de voltar */}
      <button
        onClick={() => navigate('/')}
        className="absolute top-4 left-4 text-purple-600 hover:text-purple-800 transition flex items-center"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
        <span className="text-sm font-medium">Voltar</span>
      </button>

      <h1 className="text-3xl font-bold text-center mb-8 text-purple-700">Festas Disponíveis</h1>

      {festas.length === 0 ? (
        <p className="text-center text-gray-500 text-lg py-20">
          Nenhuma festa cadastrada no sistema. Volte mais tarde!
        </p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Calendário */}
          <div className="p-4 border rounded-lg shadow-sm bg-gray-50">
            <h2 className="text-xl font-semibold mb-4 text-purple-600">Selecione uma data</h2>
            <Calendar 
              onChange={handleDateChange} 
              value={dataSelecionada}
              tileClassName={tileClassName}
              className="mx-auto border-0"
            />
            {dataSelecionada && (
              <button 
                onClick={() => setDataSelecionada(null)}
                className="mt-4 w-full bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-md transition duration-300"
              >
                Ver todas as festas
              </button>
            )}
          </div>

          {/* Lista de festas */}
          <div className="p-4 border rounded-lg shadow-sm">
            <h2 className="text-xl font-semibold mb-4 text-purple-600">
              {dataSelecionada 
                ? `Festas em ${dataSelecionada.toLocaleDateString('pt-BR')}` 
                : 'Todas as festas'}
            </h2>
            
            {festasFiltradas.length === 0 ? (
              <p className="text-gray-500 text-center py-8">
                Nenhuma festa disponível nesta data.
              </p>
            ) : (
              <ul className="space-y-4">
                {festasFiltradas.map((festa, index) => (
                  <li 
                    key={index} 
                    className="p-4 border rounded-lg hover:shadow-md transition duration-300 bg-white"
                  >
                    <div className="flex justify-between items-center">
                      <h3 className="text-lg font-semibold text-purple-700">{festa.nome}</h3>
                      <span className="text-sm bg-purple-100 text-purple-800 py-1 px-2 rounded-full">
                        {new Date(festa.data).toLocaleDateString('pt-BR')}
                      </span>
                    </div>
                    <div className="mt-2 flex justify-between items-center text-gray-600">
                      <div className="flex items-center">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                        </svg>
                        <span>{festa.local}</span>
                      </div>
                      <div className="font-semibold text-purple-700">R$ {festa.preco}</div>
                    </div>
                    <button className="mt-3 w-full bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-md transition duration-300">
                      Comprar Ingresso
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
