// src/RevendaPage.js
import { Routes, Route, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import LinkPage from './LinkPage';
import ConfirmPage from './ConfirmPage';

export default function RevendaPage() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [ticketName, setTicketName] = useState('');
  const [price, setPrice] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [showLink, setShowLink] = useState(false);

  const handleGenerateLink = () => {
    if (ticketName && price) {
      setShowLink(true);
    } else {
      alert('Preencha todos os campos antes de continuar.');
    }
  };

  const handleBackFromLink = () => {
    setShowLink(false);
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <main className="min-h-screen bg-[#0f172a] text-[#e0f2f1] font-sans">
            <header className="w-full p-6 shadow-md bg-[#1e293b] flex justify-between items-center">
              <div className="flex items-center gap-3">
                <img src="/logo.png.jpg" alt="logo" className="h-10 w-10 rounded-xl" />
                <h1 className="text-2xl font-bold tracking-tight text-[#3ddad7]">Safeswap</h1>
              </div>
              <p className="text-sm text-[#94a3b8]">Seguro e rápido</p>
            </header>

            <section className="flex flex-col items-center justify-center text-center px-6 py-16">
              {/* Seta só aparece na primeira etapa */}
              {!role && !showForm && (
                <button
                  onClick={() => navigate('/')}
                  className="self-start text-[#94a3b8] hover:text-white text-sm mb-6"
                >
                  ← Voltar
                </button>
              )}

              {!role && !showForm ? (
                <>
                  <h2 className="text-4xl font-bold mb-6 text-white">
                    Intermediação para Venda Segura de Ingressos
                  </h2>
                  <p className="text-lg text-[#cbd5e1] max-w-xl mb-8">
                    Compradores e vendedores podem usar um link único para trocar ingressos com segurança.
                    O dinheiro é retido até o comprador confirmar o recebimento.
                  </p>
                  <div className="flex gap-4">
                    <button
                      onClick={() => {
                        setRole('comprador');
                        setShowForm(true);
                      }}
                      className="bg-[#3b82f6] hover:bg-[#2563eb] px-6 py-2 rounded-xl text-white font-medium transition"
                    >
                      Sou Comprador
                    </button>
                    <button
                      onClick={() => {
                        setRole('vendedor');
                        setShowForm(true);
                      }}
                      className="bg-[#10b981] hover:bg-[#059669] px-6 py-2 rounded-xl text-white font-medium transition"
                    >
                      Sou Vendedor
                    </button>
                  </div>

                  <div className="mt-16 grid md:grid-cols-2 gap-12 max-w-4xl">
                    <div>
                      <h3 className="text-xl font-semibold mb-2 text-[#67e8f9]">Como funciona</h3>
                      <ul className="text-left list-disc list-inside text-[#cbd5e1]">
                        <li>Vendedor e comprador se conectam por um link da Safeswap</li>
                        <li>O comprador envia o pagamento para a Safeswap (via Pix)</li>
                        <li>O vendedor envia o ingresso</li>
                        <li>O comprador confirma o recebimento no site</li>
                        <li>A Safeswap libera o pagamento para o vendedor</li>
                      </ul>
                    </div>
                    <div>
                      <h3 className="text-xl font-semibold mb-2 text-[#67e8f9]">Por que usar a Safeswap?</h3>
                      <ul className="text-left list-disc list-inside text-[#cbd5e1]">
                        <li>Sem golpes – nós seguramos o dinheiro com segurança</li>
                        <li>Transferências rápidas e confiáveis</li>
                        <li>Processo simples com menos etapas</li>
                        <li>Suporte para qualquer ingresso de evento</li>
                        <li>Integrado com Pix para transações instantâneas</li>
                      </ul>
                    </div>
                  </div>
                </>
              ) : showForm && !showLink ? (
                <>
                  <button
                    onClick={() => {
                      setRole(null);
                      setShowForm(false);
                    }}
                    className="text-sm text-[#94a3b8] mb-4 self-start"
                  >
                    ← Voltar
                  </button>
                  <h2 className="text-2xl font-semibold mb-4 text-white">
                    Preencha as informações do ingresso
                  </h2>
                  <input
                    placeholder="Nome do ingresso"
                    className="mb-4 px-4 py-2 rounded text-gray-900"
                    value={ticketName}
                    onChange={(e) => setTicketName(e.target.value)}
                  />
                  <input
                    placeholder="Preço combinado (R$)"
                    className="mb-4 px-4 py-2 rounded text-gray-900"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                  />
                  <button
                    className="bg-[#3ddad7] hover:bg-[#2ec4b6] text-white font-semibold px-6 py-2 rounded-xl shadow"
                    onClick={handleGenerateLink}
                  >
                    Gerar link
                  </button>
                </>
              ) : (
                <>
                  <button onClick={handleBackFromLink} className="text-sm text-[#94a3b8] mb-4 self-start">
                    ← Voltar
                  </button>
                  <LinkPage
                    ticketName={ticketName}
                    price={price}
                    role={role}
                    setStarted={setShowLink}
                    setRole={setRole}
                    setTicketName={setTicketName}
                    setPrice={setPrice}
                  />
                </>
              )}
            </section>
          </main>
        }
      />
      <Route path="/confirmar" element={<ConfirmPage />} />
    </Routes>
  );
}
