import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [festaAberta, setFestaAberta] = useState(null);
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const ordenadas = festasJson.sort(
      (a, b) => new Date(a.data) - new Date(b.data)
    );
    setFestas(ordenadas);
  }, []);

  const handleDateChange = (date) => {
    setDataSelecionada(date);
    setFestaAberta(null);
    setMostrarCalendario(false);
  };

  const festasFiltradas = dataSelecionada
    ? festas.filter((f) => f.data === dateToStr(dataSelecionada))
    : festas;

  const dateToStr = (date) => date.toISOString().split('T')[0];

  const tileClassName = ({ date }) => {
    const dateStr = dateToStr(date);
    const existeFesta = festas.some((f) => f.data === dateStr);
    return existeFesta ? 'highlight-date' : null;
  };

  return (
    <div className="min-h-screen bg-[#0f172a] text-white px-4 py-8">
      <style>
        {`
          .react-calendar {
            background-color: #1e293b;
            color: #e0f2f1;
            border-radius: 1rem;
            border: none;
            font-family: 'Inter', sans-serif;
            padding: 1rem;
            margin-top: 1rem;
          }
          .react-calendar__tile {
            padding: 0.75rem 0.5rem;
            border-radius: 0.5rem;
            transition: background 0.2s ease;
            color: #e0f2f1;
          }
          .react-calendar__tile--now {
            background: rgba(61, 218, 215, 0.1);
          }
          .react-calendar__tile--active {
            background: #3ddad7 !important;
            color: #0f172a !important;
          }
          .react-calendar__tile:hover {
            background: rgba(61, 218, 215, 0.15);
          }
          .react-calendar__month-view__weekdays {
            text-transform: uppercase;
            font-size: 0.75rem;
            color: #94a3b8;
          }
          .react-calendar__navigation button {
            color: #58e6dc;
            background: none;
            font-weight: bold;
          }
          .highlight-date {
            background-color: #3ddad7 !important;
            color: #0f172a !important;
            font-weight: bold;
            border-radius: 0.5rem;
          }
        `}
      </style>

      {/* Botão voltar */}
      <button
        onClick={() => navigate('/')}
        className="absolute top-4 left-4 text-[#3ddad7] hover:text-[#58e6dc] transition flex items-center"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
        <span className="text-sm font-medium">Voltar</span>
      </button>

      {/* Cabeçalho */}
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-[#3ddad7]">Festas Disponíveis</h1>
        <p className="text-gray-400 mt-2">
          Clique no nome da festa para ver mais detalhes.
        </p>

        {/* Botão para mostrar calendário */}
        <div className="mt-6">
          <button
            onClick={() => setMostrarCalendario(!mostrarCalendario)}
            className="bg-[#1e293b] hover:bg-[#273549] text-[#58e6dc] px-4 py-2 rounded-lg font-medium transition"
          >
            {mostrarCalendario ? 'Esconder calendário' : 'Abrir calendário'}
          </button>
        </div>

        {mostrarCalendario && (
          <div className="max-w-sm mx-auto">
            <Calendar
              onChange={handleDateChange}
              value={dataSelecionada}
              tileClassName={tileClassName}
            />
            {dataSelecionada && (
              <button
                onClick={() => setDataSelecionada(null)}
                className="mt-2 text-sm text-[#3ddad7] underline"
              >
                Ver todas as festas
              </button>
            )}
          </div>
        )}
      </div>

      {/* Lista de festas */}
      <div className="max-w-2xl mx-auto">
        {festasFiltradas.length === 0 ? (
          <p className="text-center text-gray-400 text-lg py-20">
            Nenhuma festa disponível nesta data.
          </p>
        ) : (
          <ul className="space-y-6">
            {festasFiltradas.map((festa, index) => (
              <li
                key={index}
                className="bg-[#1e293b] p-6 rounded-2xl shadow-md border border-[#3ddad7]/10"
              >
                <button
                  onClick={() =>
                    setFestaAberta(festaAberta === index ? null : index)
                  }
                  className="flex justify-between items-center w-full text-left"
                >
                  <h3 className="text-xl font-semibold text-[#3ddad7]">{festa.nome}</h3>
                  <span className="text-sm bg-[#3ddad7]/10 text-[#58e6dc] py-1 px-3 rounded-full">
                    {new Date(festa.data).toLocaleDateString('pt-BR')}
                  </span>
                </button>

                {festaAberta === index && (
                  <div className="mt-4 space-y-3">
                    {festa.imagem && (
                      <img
                        src={festa.imagem}
                        alt={festa.nome}
                        className="w-full h-48 object-cover rounded-lg"
                      />
                    )}
                    {festa.descricao && (
                      <p className="text-gray-300 text-sm">{festa.descricao}</p>
                    )}
                    {festa.categorias && (
                      <div>
                        <h4 className="text-[#3ddad7] font-semibold mb-1">
                          Categorias:
                        </h4>
                        <ul className="text-sm space-y-1 text-gray-200">
                          {Object.entries(festa.categorias).map(([cat, preco], i) => (
                            <li key={i} className="flex justify-between">
                              <span>{cat}</span>
                              <span className="text-[#58e6dc]">R$ {preco}</span>
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                    <button className="mt-4 w-full bg-[#3ddad7] hover:bg-[#58e6dc] text-[#0f172a] font-semibold py-2 rounded-lg transition">
                      Comprar Ingresso
                    </button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
