// src/checkout.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Checkout() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const festa = state?.festa;

  if (!festa) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
        <p>Festa não selecionada. Volte e escolha uma festa para comprar ingressos.</p>
      </div>
    );
  }

  const categorias = festa.categorias;
  const [quantidades, setQuantidades] = useState(
    Object.fromEntries(Object.keys(categorias).map(cat => [cat, 0]))
  );

  const handleChange = (cat, value) => {
    const v = Math.max(0, Math.min(5, Number(value)));
    setQuantidades(prev => ({ ...prev, [cat]: v }));
  };

  const total = Object.entries(quantidades)
    .reduce((sum, [cat, qty]) => sum + qty * categorias[cat], 0);

  const handleFinalize = () => {
    navigate('/confirmar', { state: { festaName: festa.nome, items: quantidades, total } });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white p-6">
      <button onClick={() => navigate(-1)} className="mb-6 text-lg hover:underline">
        ← Voltar
      </button>
      <h1 className="text-3xl font-bold mb-4">Ingressos: {festa.nome}</h1>
      <div className="bg-gray-700 rounded-lg p-6 max-w-md mx-auto space-y-4">
        {Object.entries(categorias).map(([cat, preco]) => (
          <div key={cat} className="flex justify-between items-center">
            <div>
              <p className="font-semibold">{cat}</p>
              <p className="text-gray-400 text-sm">R$ {preco}</p>
            </div>
            <input
              type="number"
              min={0}
              max={5}
              value={quantidades[cat]}
              onChange={e => handleChange(cat, e.target.value)}
              className="w-16 bg-gray-600 text-white text-center rounded"
            />
          </div>
        ))}
        <div className="border-t border-gray-600 pt-4 flex justify-between">
          <span className="font-semibold">Total</span>
          <span className="font-bold">R$ {total}</span>
        </div>
        <button
          onClick={handleFinalize}
          disabled={total === 0}
          className="w-full py-2 bg-teal-500 text-gray-900 font-bold rounded disabled:opacity-50"
        >
          Finalizar Compra
        </button>
      </div>
    </div>
  );
}
