import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [festaAberta, setFestaAberta] = useState(null);
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const dateToStr = (date) => date.toISOString().split('T')[0];

  useEffect(() => {
    const ordenadas = festasJson.sort(
      (a, b) => new Date(a.data) - new Date(b.data)
    );
    setFestas(ordenadas);
  }, []);

  // Fechar qualquer festa aberta ao mudar filtros
  useEffect(() => {
    setFestaAberta(null);
  }, [dataSelecionada, searchTerm]);

  const handleDateChange = (date) => {
    setDataSelecionada(date);
    setMostrarCalendario(false);
  };

  const festasFiltradas = festas
    .filter((f) =>
      dataSelecionada ? f.data === dateToStr(dataSelecionada) : true
    )
    .filter((f) =>
      searchTerm
        ? f.nome.toLowerCase().includes(searchTerm.toLowerCase())
        : true
    );

  const tileClassName = ({ date }) => {
    const dateStr = dateToStr(date);
    return festas.some((f) => f.data === dateStr) ? 'highlight-date' : null;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white p-6 font-sans">
      <style>{`
        .react-calendar { background-color: rgba(255,255,255,0.1); color: #e0f2f1; border-radius: 1rem; border: none; padding: 1rem; }
        .react-calendar__tile { padding: 0.75rem 0.5rem; border-radius: 0.5rem; transition: background 0.2s ease; }
        .react-calendar__tile--now { background: rgba(61, 218, 215, 0.2); }
        .react-calendar__tile--active { background: #3ddad7 !important; color: #0f172a !important; }
        .highlight-date { background-color: #3ddad7 !important; color: #0f172a !important; font-weight: bold; }
      `}</style>

      {/* Header + Search */}
      <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8 space-y-4 sm:space-y-0">
        <div className="flex items-center space-x-2">
          <button onClick={() => navigate('/')} className="text-lg hover:underline">
            â Voltar
          </button>
          <h1 className="text-4xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-teal-300">
            Festas DisponÃ­veis
          </h1>
        </div>
        <input
          type="text"
          placeholder="ð Pesquisar festa..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full sm:w-64 bg-white bg-opacity-10 placeholder-gray-400 text-white px-4 py-2 rounded-full focus:outline-none"
        />
      </header>

      {/* Calendar Filter */}
      <div className="text-center mb-10">
        <button
          onClick={() => setMostrarCalendario(!mostrarCalendario)}
          className="inline-flex items-center space-x-2 bg-white bg-opacity-10 hover:bg-opacity-20 px-5 py-2 rounded-full font-medium transition-all"
        >
          <span role="img" aria-label="calendar">ð</span>
          <span>{mostrarCalendario ? 'Esconder calendÃ¡rio' : 'Filtrar por data'}</span>
        </button>
        {mostrarCalendario && (
          <div className="mt-6 max-w-sm mx-auto">
            <Calendar onChange={handleDateChange} value={dataSelecionada} tileClassName={tileClassName} />
            {dataSelecionada && (
              <button onClick={() => setDataSelecionada(null)} className="mt-3 text-sm underline">
                Ver todas as festas
              </button>
            )}
          </div>
        )}
      </div>

      {/* Festas List */}
      <ul className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
        {festasFiltradas.length === 0 ? (
          <li className="col-span-full text-center text-gray-400 py-20">
            Nenhuma festa encontrada.
          </li>
        ) : (
          festasFiltradas.map((festa, index) => (
            <li
              key={festa.nome + index}
              className="bg-white bg-opacity-10 backdrop-blur-md rounded-2xl p-6 shadow-lg hover:shadow-2xl transform hover:-translate-y-1 transition-all"
            >
              <button
                onClick={() => setFestaAberta(festaAberta === index ? null : index)}
                className="w-full flex justify-between items-center text-left"
              >
                <h3 className="text-xl font-semibold bg-clip-text text-transparent bg-gradient-to-r from-indigo-300 to-teal-200">
                  {festa.nome}
                </h3>
                <span className="text-xs bg-gradient-to-r from-[#3ddad7] to-[#58e6dc] text-[#0f172a] px-3 py-1 rounded-full font-bold">
                  {new Date(festa.data).toLocaleDateString('pt-BR')}
                </span>
              </button>

              {festaAberta === index && (
                <div className="mt-4 space-y-4">
                  {festa.imagem && (
                    <img src={festa.imagem} alt={festa.nome} className="w-full h-48 object-cover rounded-xl" />
                  )}
                  {festa.descricao && <p className="text-sm text-gray-200">{festa.descricao}</p>}
                  {festa.categorias && (
                    <div>
                      <h4 className="text-sm font-semibold mb-2">Categorias de Ingressos</h4>
                      <ul className="space-y-2">
                        {Object.entries(festa.categorias).map(([cat, preco], i) => (
                          <li key={i} className="flex justify-between">
                            <span>{cat}</span>
                            <span className="font-semibold">R$ {preco}</span>
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                  <button className="w-full flex items-center justify-center space-x-2 bg-gradient-to-r from-indigo-400 to-teal-300 hover:from-teal-300 hover:to-indigo-400 text-[#0f172a] font-bold py-2 rounded-full transition-all">
                    <span role="img" aria-label="ticket">ð«</span>
                    <span>Comprar Ingresso</span>
                  </button>
                </div>
              )}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}
