// src/FestaDetailPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function FestaDetailPage() {
  const navigate = useNavigate();
  const { state: { festa } = {} } = useLocation();
  const [isVisible, setIsVisible] = useState(false);
  const [isMapLoaded, setIsMapLoaded] = useState(false);
  const [activeTab, setActiveTab] = useState('sobre');

  useEffect(() => {
    // Trigger fade-in animation
    setTimeout(() => setIsVisible(true), 100);
    window.scrollTo(0, 0);
  }, []);

  if (!festa) {
    navigate('/', { replace: true });
    return null;
  }

  const displayDate = (iso) => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  // Create Google Maps URL with custom styling for a cleaner look
  const mapSrc = `https://maps.google.com/maps?q=${encodeURIComponent(festa.endereco)}&z=15&output=embed`;

  return (
    <div className="min-h-screen w-full bg-gradient-to-b from-[#0f172a] to-[#0f1a2f] text-white font-sans pb-24">
      {/* Hero Section com imagem de fundo e detalhe glassmorphism */}
      <div 
        className="relative h-[50vh] bg-cover bg-center" 
        style={{ backgroundImage: `url(${festa.imagem})` }}
      >
        <div className="absolute inset-0 bg-gradient-to-t from-[#0f172a] via-[#0f172a]/80 to-transparent"></div>
        
        {/* Botão voltar */}
        <div className="absolute top-6 left-6 z-10">
          <button
            onClick={() => navigate(-1)}
            className="group flex items-center text-white bg-black/30 backdrop-blur-md px-4 py-2 rounded-full hover:bg-teal-500/20 transition-all duration-300"
          >
            <span className="transform transition-transform group-hover:-translate-x-1">←</span>
            <span className="ml-2">Voltar</span>
          </button>
        </div>
        
        {/* Conteúdo hero centralizado */}
        <div className="absolute bottom-0 left-0 w-full p-8 z-10">
          <div className={`max-w-5xl mx-auto transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`}>
            <h1 className="text-5xl sm:text-6xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-teal-400 to-cyan-500 drop-shadow-lg">
              {festa.nome}
            </h1>
            
            {/* Badges com informações principais */}
            <div className="flex flex-wrap gap-3 mt-6">
              <div className="bg-black/40 backdrop-blur-md text-white px-4 py-2 rounded-full flex items-center space-x-2 border border-white/10">
                <span className="text-teal-400">📅</span>
                <span className="font-medium">{displayDate(festa.data)}</span>
              </div>
              <div className="bg-black/40 backdrop-blur-md text-white px-4 py-2 rounded-full flex items-center space-x-2 border border-white/10">
                <span className="text-teal-400">📍</span>
                <span className="font-medium">{festa.local}</span>
              </div>
              <div className="bg-teal-500/30 backdrop-blur-md text-white px-4 py-2 rounded-full flex items-center space-x-2 border border-teal-500/20">
                <span className="text-teal-400">✨</span>
                <span className="font-medium">Ingressos disponíveis</span>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Conteúdo principal */}
      <div className="max-w-5xl mx-auto px-4 sm:px-8 -mt-8">
        <div className={`bg-[#1e293b]/60 backdrop-blur-md rounded-3xl overflow-hidden shadow-2xl mb-12 border border-[#334155]/50 transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`}>
          
          {/* Navegação por tabs */}
          <div className="flex border-b border-[#334155]/50 overflow-x-auto hide-scrollbar">
            <button 
              onClick={() => setActiveTab('sobre')}
              className={`px-6 py-4 text-lg font-medium transition-all duration-300 ${activeTab === 'sobre' ? 'text-teal-400 border-b-2 border-teal-400' : 'text-gray-400 hover:text-white'}`}
            >
              Sobre o Evento
            </button>
            <button 
              onClick={() => setActiveTab('local')}
              className={`px-6 py-4 text-lg font-medium transition-all duration-300 ${activeTab === 'local' ? 'text-teal-400 border-b-2 border-teal-400' : 'text-gray-400 hover:text-white'}`}
            >
              Localização
            </button>
            <button 
              onClick={() => setActiveTab('ingressos')}
              className={`px-6 py-4 text-lg font-medium transition-all duration-300 ${activeTab === 'ingressos' ? 'text-teal-400 border-b-2 border-teal-400' : 'text-gray-400 hover:text-white'}`}
            >
              Ingressos
            </button>
          </div>
          
          {/* Conteúdo das tabs */}
          <div className="p-8">
            {/* Sobre o evento */}
            {activeTab === 'sobre' && (
              <div className="animate-fadeIn">
                <div className="flex items-center mb-6">
                  <div className="h-10 w-2 bg-gradient-to-b from-teal-400 to-cyan-500 rounded-full mr-4"></div>
                  <h2 className="text-2xl font-bold">Sobre o Evento</h2>
                </div>
                
                <div className="bg-[#0f172a]/60 rounded-2xl p-6 mb-6 border border-[#334155]/30">
                  <p className="text-gray-200 leading-relaxed text-lg">{festa.descricao}</p>
                </div>
                
                {/* Destaques do evento */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                  <div className="bg-gradient-to-br from-[#1e293b]/60 to-[#0f172a]/60 backdrop-blur-sm rounded-2xl p-6 border border-[#334155]/30 hover:border-teal-500/30 transition-all duration-300 hover:shadow-lg hover:shadow-teal-500/10">
                    <div className="bg-teal-500/10 rounded-full p-3 w-14 h-14 flex items-center justify-center mb-4">
                      <span className="text-teal-400 text-2xl">🎭</span>
                    </div>
                    <h3 className="text-xl font-semibold text-white mb-2">Atrações Exclusivas</h3>
                    <p className="text-gray-300">Experiências únicas para todos os gostos.</p>
                  </div>
                  
                  <div className="bg-gradient-to-br from-[#1e293b]/60 to-[#0f172a]/60 backdrop-blur-sm rounded-2xl p-6 border border-[#334155]/30 hover:border-teal-500/30 transition-all duration-300 hover:shadow-lg hover:shadow-teal-500/10">
                    <div className="bg-teal-500/10 rounded-full p-3 w-14 h-14 flex items-center justify-center mb-4">
                      <span className="text-teal-400 text-2xl">🍹</span>
                    </div>
                    <h3 className="text-xl font-semibold text-white mb-2">Área Gastronômica</h3>
                    <p className="text-gray-300">Diversas opções para todos os paladares.</p>
                  </div>
                  
                  <div className="bg-gradient-to-br from-[#1e293b]/60 to-[#0f172a]/60 backdrop-blur-sm rounded-2xl p-6 border border-[#334155]/30 hover:border-teal-500/30 transition-all duration-300 hover:shadow-lg hover:shadow-teal-500/10">
                    <div className="bg-teal-500/10 rounded-full p-3 w-14 h-14 flex items-center justify-center mb-4">
                      <span className="text-teal-400 text-2xl">🎵</span>
                    </div>
                    <h3 className="text-xl font-semibold text-white mb-2">Música ao Vivo</h3>
                    <p className="text-gray-300">Performances exclusivas para animar a noite.</p>
                  </div>
                </div>
              </div>
            )}
            
            {/* Localização */}
            {activeTab === 'local' && (
              <div className="animate-fadeIn">
                <div className="flex items-center mb-6">
                  <div className="h-10 w-2 bg-gradient-to-b from-teal-400 to-cyan-500 rounded-full mr-4"></div>
                  <h2 className="text-2xl font-bold">Localização</h2>
                </div>
                
                {/* Card com endereço estilizado */}
                <div className="bg-gradient-to-r from-[#1e293b]/80 to-[#0f172a]/80 backdrop-blur-sm p-6 rounded-2xl border border-[#334155]/30 flex items-center space-x-4 mb-6">
                  <div className="bg-teal-500/20 rounded-full p-3 flex-shrink-0 w-12 h-12 flex items-center justify-center">
                    <span className="text-teal-400 text-2xl">📌</span>
                  </div>
                  <div>
                    <h3 className="text-white font-semibold mb-1">Endereço</h3>
                    <p className="text-gray-300 tracking-wide">{festa.endereco}</p>
                  </div>
                </div>
                
                {/* Mapa com design melhorado */}
                <div className="relative overflow-hidden rounded-2xl border border-[#334155]/30 shadow-xl transition-all duration-300 hover:shadow-teal-400/10">
                  {/* Overlay de carregamento */}
                  <div className={`absolute inset-0 bg-[#0f172a] bg-opacity-70 backdrop-blur-sm flex items-center justify-center z-20 transition-opacity duration-500 ${isMapLoaded ? 'opacity-0 pointer-events-none' : 'opacity-100'}`}>
                    <div className="flex flex-col items-center">
                      <div className="w-10 h-10 border-4 border-teal-400 border-t-transparent rounded-full animate-spin mb-3"></div>
                      <div className="text-teal-400 font-medium">Carregando mapa...</div>
                    </div>
                  </div>
                  
                  {/* Container do mapa com altura melhorada */}
                  <div className="w-full h-72 sm:h-80 md:h-96 overflow-hidden">
                    <iframe
                      title="Localização do evento"
                      src={mapSrc}
                      className="w-full h-full bg-[#0f172a]"
                      allowFullScreen
                      loading="lazy"
                      onLoad={() => setIsMapLoaded(true)}
                      referrerPolicy="no-referrer-when-downgrade"
                    />
                  </div>
                  
                  {/* Botão para abrir no Google Maps */}
                  <a 
                    href={`https://maps.google.com/maps?q=${encodeURIComponent(festa.endereco)}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="absolute bottom-4 right-4 z-30 bg-black/70 backdrop-blur-sm hover:bg-teal-500/30 text-white font-medium px-4 py-2 rounded-full transition-all duration-300 flex items-center space-x-2 border border-white/10"
                  >
                    <span>Abrir no Google Maps</span>
                    <span className="text-xs">↗</span>
                  </a>
                </div>
                
                {/* Informações adicionais de acesso */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
                  <div className="bg-gradient-to-br from-[#1e293b]/60 to-[#0f172a]/60 backdrop-blur-sm rounded-2xl p-6 border border-[#334155]/30">
                    <h3 className="text-xl font-semibold text-teal-400 mb-3">Estacionamento</h3>
                    <p className="text-gray-300">Estacionamento disponível no local com manobrista.</p>
                  </div>
                  
                  <div className="bg-gradient-to-br from-[#1e293b]/60 to-[#0f172a]/60 backdrop-blur-sm rounded-2xl p-6 border border-[#334155]/30">
                    <h3 className="text-xl font-semibold text-teal-400 mb-3">Transporte Público</h3>
                    <p className="text-gray-300">Próximo a estações de metrô e pontos de ônibus.</p>
                  </div>
                </div>
              </div>
            )}
            
            {/* Ingressos */}
            {activeTab === 'ingressos' && (
              <div className="animate-fadeIn">
                <div className="flex items-center mb-6">
                  <div className="h-10 w-2 bg-gradient-to-b from-teal-400 to-cyan-500 rounded-full mr-4"></div>
                  <h2 className="text-2xl font-bold">Categorias de Ingressos</h2>
                </div>
                
                <div className="space-y-4">
                  {Object.entries(festa.categorias).map(([cat, preco], idx) => (
                    <div
                      key={cat}
                      className="bg-gradient-to-r from-[#1e293b]/60 to-[#0f172a]/60 backdrop-blur-sm rounded-2xl p-6 border border-[#334155]/30 hover:border-teal-500/30 transition-all duration-300 hover:shadow-lg hover:shadow-teal-500/5"
                    >
                      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                        <div className="flex items-center">
                          <div className="bg-teal-500/20 rounded-full p-3 w-12 h-12 flex items-center justify-center mr-4">
                            <span className="text-teal-400 text-xl">🎟️</span>
                          </div>
                          <div>
                            <h3 className="text-xl font-semibold text-white">{cat}</h3>
                            <p className="text-gray-400 text-sm">Disponibilidade limitada</p>
                          </div>
                        </div>
                        <div className="flex items-center gap-4">
                          <div className="bg-gradient-to-r from-teal-500/20 to-cyan-500/20 px-6 py-3 rounded-xl">
                            <span className="font-bold text-xl text-white">R$ {preco}</span>
                          </div>
                          <button className="bg-teal-500/20 hover:bg-teal-500/40 text-teal-400 px-4 py-2 rounded-xl font-medium transition-all duration-300 border border-teal-500/30">
                            Selecionar
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
                
                {/* Informações sobre ingressos */}
                <div className="mt-8">
                  <div className="bg-teal-500/10 rounded-2xl p-6 border border-teal-500/20">
                    <h3 className="text-lg font-semibold flex items-center mb-4">
                      <span className="text-teal-400 mr-2">ℹ️</span> 
                      <span>Informações importantes</span>
                    </h3>
                    <ul className="space-y-2 text-gray-300">
                      <li className="flex items-start">
                        <span className="text-teal-400 mr-2">•</span>
                        <span>Ingressos não são reembolsáveis.</span>
                      </li>
                      <li className="flex items-start">
                        <span className="text-teal-400 mr-2">•</span>
                        <span>É necessário apresentar documento com foto na entrada.</span>
                      </li>
                      <li className="flex items-start">
                        <span className="text-teal-400 mr-2">•</span>
                        <span>A troca de titularidade pode ser realizada até 48h antes do evento.</span>
                      </li>
                    </ul>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Botão de compra fixo redesenhado */}
      <div className="fixed bottom-0 left-0 right-0 z-50 px-4 py-4 bg-[#0f172a]/90 backdrop-blur-md border-t border-[#334155]/50">
        <div className="max-w-5xl mx-auto flex flex-col sm:flex-row items-center justify-between gap-4">
          <div className="hidden sm:block">
            <h3 className="text-lg font-semibold text-white">{festa.nome}</h3>
            <p className="text-gray-400">{displayDate(festa.data)} • {festa.local}</p>
          </div>
          
          <div className="w-full sm:w-auto">
            <button
              onClick={() => navigate('/checkout', { state: { festa } })}
              className="w-full sm:w-auto px-8 py-3 bg-gradient-to-r from-teal-500 to-cyan-500 text-white font-bold text-lg rounded-xl shadow-xl transition-all duration-300 hover:shadow-teal-400/30 hover:shadow-lg hover:-translate-y-1 flex items-center justify-center"
            >
              <span className="mr-2">🎫</span> Comprar Ingresso
            </button>
          </div>
        </div>
      </div>

      {/* Decoração de fundo melhorada */}
      <div className="fixed top-0 left-0 w-full h-full overflow-hidden pointer-events-none -z-10">
        <div className="absolute top-0 left-0 w-full h-full bg-[#0f172a] opacity-90"></div>
        <div className="absolute top-1/4 left-[-10%] w-80 h-80 bg-teal-400 rounded-full filter blur-[120px] opacity-15 animate-pulse-slow"></div>
        <div className="absolute bottom-[-10%] right-[-5%] w-96 h-96 bg-cyan-500 rounded-full filter blur-[120px] opacity-15 animate-pulse-slow delay-1000"></div>
        <div className="absolute top-[-10%] right-[10%] w-64 h-64 bg-purple-500 rounded-full filter blur-[120px] opacity-10 animate-pulse-slow delay-2000"></div>
      </div>

      {/* Adiciona ao CSS global */}
      <style jsx global>{`
        @keyframes pulse-slow {
          0%, 100% { opacity: 0.1; }
          50% { opacity: 0.2; }
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-pulse-slow {
          animation: pulse-slow 8s infinite;
        }
        .animate-fadeIn {
          animation: fadeIn 0.5s ease-out forwards;
        }
        .hide-scrollbar::-webkit-scrollbar {
          display: none;
        }
        .hide-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
      `}</style>
    </div>
  );
}
