import React, { useEffect, useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [modalFesta, setModalFesta] = useState(null);

  useEffect(() => {
    // Ordena pelo campo data
    const sorted = [...festasJson].sort((a, b) => new Date(a.data) - new Date(b.data));
    setFestas(sorted);
  }, []);

  const formatDate = iso => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  const filtered = festas
    .filter(f => (dataSelecionada ? f.data === dataSelecionada.toISOString().split('T')[0] : true))
    .filter(f => f.nome.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      {/* Busca e calendário */}
      <div className="flex flex-col md:flex-row items-center justify-between mb-8 gap-4">
        <input
          type="text"
          placeholder="🔍 Buscar festa..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          className="w-full md:w-1/3 px-4 py-2 rounded-lg bg-gray-800 placeholder-gray-500 focus:outline-none"
        />
        <Calendar
          onChange={setDataSelecionada}
          value={dataSelecionada}
          tileClassName={({ date }) =>
            festas.some(f => f.data === date.toISOString().split('T')[0])
              ? 'bg-teal-500 text-gray-900 rounded'
              : undefined
          }
          className="bg-gray-800 p-2 rounded-lg"
        />
      </div>

      {/* Grid de cartões */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {filtered.length === 0 && (
          <p className="col-span-full text-center text-gray-500">Nenhuma festa encontrada.</p>
        )}
        {filtered.map((f, idx) => (
          <div
            key={f.nome + idx}
            className="bg-gray-800 rounded-lg overflow-hidden shadow-lg hover:shadow-2xl transition cursor-pointer"
            onClick={() => setModalFesta(f)}
          >
            {f.imagem && (
              <img src={f.imagem} alt={f.nome} className="w-full h-40 object-cover" />
            )}
            <div className="p-4">
              <h3 className="text-xl font-semibold mb-1">{f.nome}</h3>
              <p className="text-gray-400 mb-2">{formatDate(f.data)}</p>
              <p className="text-sm text-gray-300 truncate">{f.descricao}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Modal de detalhe */}
      {modalFesta && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
          <div className="bg-gray-800 rounded-lg max-w-md w-full p-6 relative">
            <button
              onClick={() => setModalFesta(null)}
              className="absolute top-2 right-2 text-gray-400 hover:text-white"
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold mb-2">{modalFesta.nome}</h2>
            <p className="text-gray-400 mb-4">{formatDate(modalFesta.data)}</p>
            {modalFesta.imagem && (
              <img src={modalFesta.imagem} alt={modalFesta.nome} className="w-full h-48 object-cover rounded mb-4" />
            )}
            <p className="text-gray-300 mb-4">{modalFesta.descricao}</p>
            {modalFesta.categorias && (
              <ul className="text-gray-200 mb-4">
                {Object.entries(modalFesta.categorias).map(([cat, preco]) => (
                  <li key={cat} className="flex justify-between">
                    <span>{cat}</span>
                    <span>R$ {preco}</span>
                  </li>
                ))}
              </ul>
            )}
            <button
              onClick={() => alert('Comprar ingressos...')}
              className="w-full py-2 bg-teal-500 rounded-lg text-gray-900 font-bold"
            >
              Comprar Ingresso
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
