import { Routes, Route, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import LinkPage from './LinkPage';
import ConfirmPage from './ConfirmPage';

export default function App() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [ticketName, setTicketName] = useState('');
  const [price, setPrice] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [showLink, setShowLink] = useState(false);

  const handleGenerateLink = () => {
    if (ticketName && price) {
      setShowLink(true);
    } else {
      alert('Preencha todos os campos antes de continuar.');
    }
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <main className="min-h-screen bg-gray-900 text-white font-sans">
            <header className="w-full p-6 shadow-md bg-gray-950 flex justify-between items-center">
              <div className="flex items-center gap-3">
                <img src="/logo.png.jpg" alt="logo" className="h-10 w-10 rounded-xl" />
                <h1 className="text-2xl font-bold tracking-tight">Safeswap</h1>
              </div>
              <p className="text-sm text-gray-400">Seguro e rápido</p>
            </header>

            <section className="flex flex-col items-center justify-center text-center px-6 py-16">
              {!role && !showForm ? (
                <>
                  <h2 className="text-4xl font-bold mb-6">Intermediação para Venda Segura de Ingressos</h2>
                  <p className="text-lg text-gray-300 max-w-xl mb-8">
                    Compradores e vendedores podem usar um link único para trocar ingressos com segurança.
                    O dinheiro é retido até o comprador confirmar o recebimento.
                  </p>
                  <div className="flex gap-4">
                    <button onClick={() => { setRole('comprador'); setShowForm(true); }} className="bg-blue-600 px-6 py-2 rounded-xl">Sou Comprador</button>
                    <button onClick={() => { setRole('vendedor'); setShowForm(true); }} className="bg-green-600 px-6 py-2 rounded-xl">Sou Vendedor</button>
                  </div>

                  <div className="mt-16 grid md:grid-cols-2 gap-12 max-w-4xl">
                    <div>
                      <h3 className="text-xl font-semibold mb-2">Como funciona</h3>
                      <ul className="text-left list-disc list-inside text-gray-300">
                        <li>Vendedor e comprador se conectam por um link da Safeswap</li>
                        <li>O comprador envia o pagamento para a Safeswap (via Pix)</li>
                        <li>O vendedor envia o ingresso</li>
                        <li>O comprador confirma o recebimento no site</li>
                        <li>A Safeswap libera o pagamento para o vendedor</li>
                      </ul>
                    </div>
                    <div>
                      <h3 className="text-xl font-semibold mb-2">Por que usar a Safeswap?</h3>
                      <ul className="text-left list-disc list-inside text-gray-300">
                        <li>Sem golpes – nós seguramos o dinheiro com segurança</li>
                        <li>Transferências rápidas e confiáveis</li>
                        <li>Processo simples com menos etapas</li>
                        <li>Suporte para qualquer ingresso de evento</li>
                        <li>Integrado com Pix para transações instantâneas</li>
                      </ul>
                    </div>
                  </div>
                </>
              ) : showForm && !showLink ? (
                <>
                  <button onClick={() => { setRole(null); setShowForm(false); }} className="self-start text-left text-sm text-gray-300 mb-4">← Voltar</button>
                  <h2 className="text-2xl font-semibold mb-4">Preencha as informações do ingresso</h2>
                  <input
                    placeholder="Nome do ingresso"
                    className="mb-4 px-4 py-2 rounded text-black"
                    value={ticketName}
                    onChange={(e) => setTicketName(e.target.value)}
                  />
                  <input
                    placeholder="Preço combinado (R$)"
                    className="mb-4 px-4 py-2 rounded text-black"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                  />
                  <button
                    className="bg-white text-gray-900 font-semibold px-6 py-2 rounded-xl shadow"
                    onClick={handleGenerateLink}
                  >
                    Gerar link
                  </button>
                </>
              ) : (
                <LinkPage
                  ticketName={ticketName}
                  price={price}
                  role={role}
                  setStarted={() => {}}
                  setRole={setRole}
                  setTicketName={setTicketName}
                  setPrice={setPrice}
                />
              )}
            </section>
          </main>
        }
      />

      <Route path="/confirmar" element={<ConfirmPage />} />

      <Route
        path="/pagamento"
        element={
          <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center text-center">
            <h1 className="text-3xl font-bold mb-2">Pagamento</h1>
            <p className="text-gray-400">Agora a outra parte deve transferir o valor por Pix.</p>
          </div>
        }
      />

      <Route
        path="/cancelado"
        element={
          <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center text-center">
            <h1 className="text-3xl font-bold mb-2">Operação cancelada</h1>
            <p className="text-gray-400">O outro usuário recusou a transação.</p>
          </div>
        }
      />
    </Routes>
  );
}
