import { useEffect, useState } from 'react';
import { Calendar as CalendarIcon, Clock, ChevronDown, ChevronUp, ArrowLeft, Users } from 'lucide-react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [festaAberta, setFestaAberta] = useState(null);
  const [dataSelecionada, setDataSelecionada] = useState(null);

  useEffect(() => {
    const ordenadas = festasJson.sort((a, b) => new Date(a.data) - new Date(b.data));
    setFestas(ordenadas);
  }, []);

  const dateToStr = (date) => date.toISOString().split('T')[0];

  const festasFiltradas = dataSelecionada
    ? festas.filter((f) => f.data === dateToStr(dataSelecionada))
    : festas;

  const navigate = (path) => {
    console.log(`Navegando para: ${path}`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-900 to-slate-800 text-white">
      <div className="relative">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-600 to-teal-500 opacity-90"></div>
        <div className="absolute inset-0 opacity-20 bg-slate-800"></div>

        <div className="relative z-10 pt-16 pb-8 px-6">
          <button
            onClick={() => navigate('/')}
            className="absolute top-4 left-4 text-white hover:text-gray-200 transition flex items-center bg-black/20 rounded-lg px-3 py-2"
          >
            <ArrowLeft className="h-5 w-5 mr-1" />
            <span className="font-medium">Voltar</span>
          </button>

          <div className="text-center">
            <h1 className="text-5xl font-extrabold text-white">Festas Disponíveis</h1>
            <p className="text-white/80 mt-4 font-medium max-w-lg mx-auto">
              Clique no nome da festa para ver mais detalhes e garantir seu ingresso.
            </p>
            <div className="mt-6 max-w-sm mx-auto">
              <Calendar
                onChange={(date) => {
                  setDataSelecionada(date);
                  setFestaAberta(null);
                }}
                value={dataSelecionada}
              />
              {dataSelecionada && (
                <button
                  className="mt-3 text-cyan-400 underline"
                  onClick={() => setDataSelecionada(null)}
                >
                  Ver todas as festas
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-3xl mx-auto px-4 py-12 -mt-6">
        {festasFiltradas.length === 0 ? (
          <div className="flex flex-col items-center justify-center py-20 text-center bg-slate-800/70 rounded-2xl shadow-lg">
            <CalendarIcon className="h-16 w-16 text-cyan-400 mb-4 opacity-70" />
            <p className="text-2xl font-bold text-white mb-2">Nenhuma festa disponível</p>
            <p className="text-gray-400">Volte em breve para conferir os próximos eventos.</p>
          </div>
        ) : (
          <ul className="space-y-6">
            {festasFiltradas.map((festa, index) => (
              <li
                key={index}
                className="bg-slate-800/80 rounded-xl shadow-lg overflow-hidden border border-cyan-500/20 hover:border-cyan-500/40 transition-all duration-300"
              >
                <button
                  onClick={() => setFestaAberta(festaAberta === index ? null : index)}
                  className="flex justify-between items-center w-full text-left p-6"
                >
                  <div className="flex items-center">
                    <div className="h-12 w-12 flex items-center justify-center bg-cyan-500/20 text-cyan-400 rounded-full mr-4">
                      <CalendarIcon className="h-6 w-6" />
                    </div>
                    <div>
                      <h3 className="text-xl font-bold text-white">{festa.nome}</h3>
                      <div className="flex items-center text-cyan-300 mt-1 text-sm">
                        <Clock className="h-4 w-4 mr-1" />
                        <span>{festa.data.split('-').reverse().join('/')}</span>
                      </div>
                    </div>
                  </div>
                  {festaAberta === index ? (
                    <ChevronUp className="h-5 w-5 text-cyan-400" />
                  ) : (
                    <ChevronDown className="h-5 w-5 text-cyan-400" />
                  )}
                </button>

                {festaAberta === index && (
                  <div className="px-6 pb-6">
                    <div className="h-px bg-gradient-to-r from-cyan-500/5 via-cyan-500/40 to-cyan-500/5 mb-6"></div>
                    <div className="space-y-6">
                      {festa.imagem && (
                        <div className="rounded-lg overflow-hidden shadow-lg">
                          <img
                            src={festa.imagem}
                            alt={festa.nome}
                            className="w-full h-56 object-cover"
                          />
                        </div>
                      )}
                      {festa.descricao && (
                        <div className="bg-slate-900/40 rounded-lg p-4">
                          <p className="text-gray-300">{festa.descricao}</p>
                        </div>
                      )}
                      {festa.categorias && (
                        <div className="bg-slate-900/40 rounded-lg p-4">
                          <div className="flex items-center gap-2 mb-3 text-cyan-400 font-semibold">
                            <Users className="h-5 w-5" />
                            <h4>Categorias de Ingressos</h4>
                          </div>
                          <ul className="divide-y divide-slate-700/60">
                            {Object.entries(festa.categorias).map(([cat, preco], i) => (
                              <li key={i} className="flex justify-between py-3 first:pt-0 last:pb-0">
                                <span className="text-white font-medium">{cat}</span>
                                <span className="text-cyan-300 font-bold">R$ {preco}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                      <button className="mt-2 w-full bg-gradient-to-r from-cyan-500 to-teal-400 hover:from-cyan-600 hover:to-teal-500 text-white font-bold py-3 px-4 rounded-lg shadow-lg flex items-center justify-center">
                        Comprar Ingresso
                      </button>
                    </div>
                  </div>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
