import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [festaAberta, setFestaAberta] = useState(null);
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const navigate = useNavigate();

  const dateToStr = (date) => date.toISOString().split('T')[0];

  useEffect(() => {
    const ordenadas = festasJson.sort((a, b) => new Date(a.data) - new Date(b.data));
    setFestas(ordenadas);
  }, []);

  const handleDateChange = (date) => {
    setDataSelecionada(date);
    setFestaAberta(null);
    setMostrarCalendario(false);
  };

  const festasFiltradas = dataSelecionada
    ? festas.filter((f) => f.data === dateToStr(dataSelecionada))
    : festas;

  const tileClassName = ({ date }) => {
    const dateStr = dateToStr(date);
    const existeFesta = festas.some((f) => f.data === dateStr);
    return existeFesta ? 'highlight-date' : null;
  };

  return (
    <div className="min-h-screen bg-[#0f172a] text-white px-4 py-10 font-sans">
      <style>{`
        .react-calendar {
          background-color: #1e293b;
          color: #e0f2f1;
          border-radius: 1rem;
          border: none;
          font-family: 'Inter', sans-serif;
          padding: 1rem;
        }
        .react-calendar__tile {
          padding: 0.75rem 0.5rem;
          border-radius: 0.5rem;
          transition: background 0.2s ease;
        }
        .react-calendar__tile--now {
          background: rgba(61, 218, 215, 0.1);
        }
        .react-calendar__tile--active {
          background: #3ddad7 !important;
          color: #0f172a !important;
        }
        .highlight-date {
          background-color: #3ddad7 !important;
          color: #0f172a !important;
          font-weight: bold;
        }
      `}</style>

      {/* CabeÃ§alho */}
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-[#3ddad7]">Festas DisponÃ­veis</h1>
        <button
          onClick={() => navigate('/')}
          className="text-[#58e6dc] text-sm hover:underline"
        >
          Voltar ao inÃ­cio
        </button>
      </div>

      {/* CalendÃ¡rio */}
      <div className="text-center mb-10">
        <button
          onClick={() => setMostrarCalendario(!mostrarCalendario)}
          className="bg-[#1e293b] hover:bg-[#273549] text-[#58e6dc] px-4 py-2 rounded-lg font-medium transition"
        >
          {mostrarCalendario ? 'Esconder calendÃ¡rio' : 'Filtrar por data'}
        </button>
        {mostrarCalendario && (
          <div className="mt-6 max-w-sm mx-auto">
            <Calendar
              onChange={handleDateChange}
              value={dataSelecionada}
              tileClassName={tileClassName}
            />
            {dataSelecionada && (
              <button
                onClick={() => setDataSelecionada(null)}
                className="mt-2 text-sm text-[#3ddad7] underline"
              >
                Ver todas as festas
              </button>
            )}
          </div>
        )}
      </div>

      {/* Lista de festas */}
      <div className="max-w-3xl mx-auto">
        {festasFiltradas.length === 0 ? (
          <div className="text-center text-gray-400 text-lg py-20">
            Nenhuma festa disponÃ­vel nesta data.
          </div>
        ) : (
          <ul className="space-y-6">
            {festasFiltradas.map((festa, index) => (
              <li
                key={index}
                className="bg-[#1e293b] rounded-xl border border-[#3ddad7]/10 shadow-md transition hover:border-[#3ddad7]/30"
              >
                <button
                  onClick={() => setFestaAberta(festaAberta === index ? null : index)}
                  className="w-full px-6 py-5 flex justify-between items-center text-left group"
                >
                  <h3 className="text-lg font-semibold text-[#3ddad7] group-hover:underline">
                    {festa.nome}
                  </h3>
                  <span className="text-xs bg-[#3ddad7]/10 text-[#58e6dc] px-3 py-1 rounded-full">
                    {new Date(festa.data).toLocaleDateString('pt-BR')}
                  </span>
                </button>

                {festaAberta === index && (
                  <div className="px-6 pb-6">
                    {festa.imagem && (
                      <img
                        src={festa.imagem}
                        alt={festa.nome}
                        className="w-full h-48 object-cover rounded-lg mb-4"
                      />
                    )}
                    {festa.descricao && (
                      <p className="text-sm text-gray-300 mb-3">{festa.descricao}</p>
                    )}
                    {festa.categorias && (
                      <div className="mb-4">
                        <h4 className="text-sm font-semibold text-[#3ddad7] mb-2">
                          Categorias de Ingressos
                        </h4>
                        <ul className="text-sm space-y-1 text-white">
                          {Object.entries(festa.categorias).map(([cat, preco], i) => (
                            <li key={i} className="flex justify-between">
                              <span>{cat}</span>
                              <span className="text-[#58e6dc] font-semibold">R$ {preco}</span>
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                    <button className="w-full bg-[#3ddad7] hover:bg-[#58e6dc] text-[#0f172a] font-bold py-2 rounded-lg transition">
                      Comprar Ingresso
                    </button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
