// src/LinkPage.js
import { useNavigate } from 'react-router-dom';

export default function LinkPage({ ticketName, price, role, setRole, setStarted, setTicketName, setPrice }) {
  const navigate = useNavigate();

  const link = `http://localhost:3000/confirmar?ingresso=${encodeURIComponent(ticketName)}&valor=${encodeURIComponent(price)}&role=${role}`;
  const instruction = role === 'comprador' 
    ? 'Copie esse link e envie para o vendedor!' 
    : 'Copie esse link e envie para o comprador!';

  const handleBack = () => {
    setPrice('');
    setTicketName('');
    setStarted(false);
    setRole(null);
  };

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(link);
      alert("Link copiado para a área de transferência!");
    } catch (err) {
      alert("Erro ao copiar link.");
    }
  };

  return (
    <main className="min-h-screen bg-gray-900 text-white font-sans">
      <header className="w-full p-6 shadow-md bg-gray-950 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src="/logo.png.jpg" alt="logo" className="h-10 w-10 rounded-xl" />
          <h1 className="text-2xl font-bold tracking-tight">Safeswap</h1>
        </div>
        <p className="text-sm text-gray-400">Seguro e rápido</p>
      </header>

      <section className="px-6 py-16 text-center flex flex-col items-center gap-4">
        <button
          onClick={handleBack}
          className="bg-gray-700 text-white px-4 py-2 rounded shadow hover:bg-gray-600 self-start"
        >
          ← Voltar
        </button>

        <h2 className="text-xl font-semibold">{instruction}</h2>
        <p className="text-sm text-gray-400">{link}</p>

        <button
          onClick={handleCopy}
          className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded shadow"
        >
          Copiar link
        </button>

        <div className="grid md:grid-cols-2 gap-12 max-w-4xl mt-16 text-left">
          <div>
            <h3 className="text-lg font-bold mb-2">Como funciona</h3>
            <ul className="list-disc list-inside text-gray-300">
              <li>Vendedor e comprador se conectam por um link da Safeswap</li>
              <li>O comprador envia o pagamento para a Safeswap (via Pix)</li>
              <li>O vendedor envia o ingresso</li>
              <li>O comprador confirma o recebimento no site</li>
              <li>A Safeswap libera o pagamento para o vendedor</li>
            </ul>
          </div>

          <div>
            <h3 className="text-lg font-bold mb-2">Por que usar a Safeswap?</h3>
            <ul className="list-disc list-inside text-gray-300">
              <li>Sem golpes – nós seguramos o dinheiro com segurança</li>
              <li>Transferências rápidas e confiáveis</li>
              <li>Processo simples com menos etapas</li>
              <li>Suporte para qualquer ingresso de evento</li>
              <li>Integrado com Pix</li>
            </ul>
          </div>
        </div>
      </section>
    </main>
  );
}
