// src/FestasPage.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [openIndex, setOpenIndex] = useState(null);
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  // Ordena festas ao montar
  useEffect(() => {
    setFestas([...festasJson].sort((a, b) => new Date(a.data) - new Date(b.data)));
  }, []);

  // Fechar ao mudar filtros
  useEffect(() => {
    setOpenIndex(null);
  }, [dataSelecionada, searchTerm]);

  // Formatar data string YYYY-MM-DD para DD/MM/YYYY
  const displayDate = (iso) => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  // Filtros
  const festasFiltradas = festas
    .filter(f => (dataSelecionada ? f.data === dataSelecionada.toISOString().split('T')[0] : true))
    .filter(f => f.nome.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white p-6 font-sans">

      {/* Header */}
      <header className="flex justify-between items-center mb-8">
        <h1 className="flex items-baseline space-x-3 text-4xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-teal-300">
          <span onClick={() => navigate('/')} className="cursor-pointer">‚Üê</span>
          <span>Festas Dispon√≠veis</span>
        </h1>
        <input
          type="text"
          placeholder="üîç Pesquisar festa..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full max-w-xs bg-white bg-opacity-10 placeholder-gray-400 text-white px-4 py-2 rounded-full"
        />
      </header>

      {/* Calend√°rio */}
      <div className="text-center mb-10">
        <button
          onClick={() => setMostrarCalendario(vis => !vis)}
          className="inline-flex items-center px-5 py-2 bg-white bg-opacity-10 rounded-full"
        >üìÖ {mostrarCalendario ? 'Esconder calend√°rio' : 'Filtrar por data'}</button>
        {mostrarCalendario && (
          <div className="mt-4 max-w-sm mx-auto">
            <Calendar
              onChange={setDataSelecionada}
              value={dataSelecionada}
              tileClassName={({ date }) => festas.some(f => f.data === date.toISOString().split('T')[0]) ? 'highlight-date' : ''}
            />
            {dataSelecionada && <button onClick={() => setDataSelecionada(null)} className="mt-2 text-sm underline">Ver todas as festas</button>}
          </div>
        )}
      </div>

      {/* Lista de festas */}
      <ul className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
        {festasFiltradas.length === 0 && (<li className="col-span-full text-center text-gray-400 py-20">Nenhuma festa encontrada.</li>)}
        {festasFiltradas.map((f, idx) => {
          const isOpen = openIndex === idx;
          return (
            <li key={idx} className={`bg-white bg-opacity-10 rounded-xl p-4 ${isOpen ? 'shadow-2xl' : 'shadow-md'} transition`}>
              <div className="flex justify-between items-center">
                <button onClick={() => setOpenIndex(isOpen ? null : idx)} className="text-left flex-1">
                  <h3 className="text-xl font-semibold text-[#3ddad7]">{f.nome}</h3>
                </button>
                <span className="text-xs bg-[#3ddad7]/20 text-[#3ddad7] px-3 py-1 rounded-full">{displayDate(f.data)}</span>
              </div>
              {isOpen && (
                <div className="mt-4 space-y-4">
                  {f.imagem && <img src={f.imagem} alt="" className="w-full h-48 object-cover rounded-lg" />}
                  <p className="text-gray-200">{f.descricao}</p>
                  <ul className="space-y-1">
                    {Object.entries(f.categorias).map(([cat, preco]) => (
                      <li key={cat} className="flex justify-between">
                        <span>{cat}</span><span>R$ {preco}</span>
                      </li>
                    ))}
                  </ul>
                  <button className="w-full py-2 bg-gradient-to-r from-[#3ddad7] to-[#58e6dc] text-[#0f172a] rounded-full font-bold">Comprar Ingresso</button>
                </div>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
}
