// src/FestaDetailPage.js
import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function FestaDetailPage() {
  const navigate = useNavigate();
  const { state: { festa } = {} } = useLocation();

  if (!festa) {
    // Se n√£o vier festa no state, redireciona de volta
    navigate('/', { replace: true });
    return null;
  }

  const displayDate = (iso) => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white p-8 font-sans max-w-4xl mx-auto">
      <button
        onClick={() => navigate(-1)}
        className="text-lg mb-6 flex items-center text-gray-300 hover:text-white"
      >
        ‚Üê Voltar
      </button>

      <div className="bg-white bg-opacity-10 backdrop-blur-md rounded-2xl overflow-hidden shadow-lg">
        {festa.imagem && (
          <img
            src={festa.imagem}
            alt={festa.nome}
            className="w-full h-64 object-cover"
          />
        )}
        <div className="p-8 space-y-4">
          <h1 className="text-4xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-teal-300">
            {festa.nome}
          </h1>
          <div className="flex flex-wrap gap-4 text-gray-300">
            <span>üìÖ {displayDate(festa.data)}</span>
            <span>üìç {festa.local}</span>
          </div>
          <p className="text-gray-200 leading-relaxed">
            {festa.descricao}
          </p>

          <h2 className="text-2xl font-semibold mt-6">Categorias de Ingressos</h2>
          <ul className="space-y-2">
            {Object.entries(festa.categorias).map(([cat, preco]) => (
              <li key={cat} className="flex justify-between">
                <span className="font-medium">{cat}</span>
                <span className="font-bold">R$ {preco}</span>
              </li>
            ))}
          </ul>

          <button
            onClick={() => navigate('/checkout', { state: { festa } })}
            className="mt-8 w-full inline-block text-center bg-gradient-to-r from-indigo-400 to-teal-300 text-[#0f172a] font-bold py-3 rounded-full"
          >
            üé´ Comprar Ingresso
          </button>
        </div>
      </div>
    </div>
  );
}
