import { useLocation } from 'react-router-dom';
import { QRCode } from 'qrcode.react'; // ← aqui está o QR code real

export default function Pagamento() {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const price = searchParams.get('valor');

  // Link que será convertido no QR (pode ser um Pix Copia e Cola real no futuro)
  const qrValue = `Pagamento SafeSwap: R$ ${price}`;

  return (
    <main className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center text-center px-4">
      <h1 className="text-3xl font-bold mb-4">Escolha como pagar</h1>
      <p className="mb-6 text-lg">Valor: <strong>R$ {price}</strong></p>

      <div className="flex flex-col md:flex-row gap-6">
        {/* QR Code real para Pix (placeholder) */}
        <div className="bg-white p-4 rounded-xl text-black flex flex-col items-center">
          <p className="font-semibold mb-2">Pix (QR Code)</p>
          <QRCode value={qrValue} size={192} /> {/* 192px = 12rem */}
        </div>

        {/* Pagamento por cartão de crédito */}
        <div className="bg-white p-4 rounded-xl text-black flex flex-col items-center">
          <p className="font-semibold mb-2">Cartão de Crédito</p>
          <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700">
            Pagar com cartão
          </button>
        </div>
      </div>
    </main>
  );
}
