// src/LinkPage.js
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function LinkPage({ ticketName, price, role, setStarted, setRole, setTicketName, setPrice }) {
  const navigate = useNavigate();
  const baseUrl = window.location.origin;
  const generatedLink = `${baseUrl}/#/confirmar?ingresso=${encodeURIComponent(ticketName)}&valor=${encodeURIComponent(price)}&role=${encodeURIComponent(role)}&source=${encodeURIComponent(role)}`;
  const sendText = role === 'comprador' ? 'vendedor' : 'comprador';

  useEffect(() => {
    if (role !== 'comprador') return; // só escuta se o comprador gerou o link

    const channel = new BroadcastChannel('safeswap_channel');
    channel.onmessage = (event) => {
      if (event.data === 'go_to_payment') {
        navigate(`/pagamento?valor=${encodeURIComponent(price)}`);
      }
    };

    return () => channel.close();
  }, [role, navigate, price]);

  const copyLink = () => {
    navigator.clipboard.writeText(generatedLink);
    alert('Link copiado para a área de transferência!');
  };

  return (
    <div className="text-white px-6 py-10 text-center flex flex-col items-center gap-6">
      <h2 className="text-2xl font-bold">Copie esse link e envie para o {sendText}!</h2>

      <p className="text-blue-400 break-words max-w-full">{generatedLink}</p>
      <button
        className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-xl"
        onClick={copyLink}
      >
        Copiar link
      </button>

      <div className="mt-10 flex flex-col md:flex-row gap-10 justify-center text-left max-w-4xl w-full">
        <div>
          <h3 className="text-lg font-bold mb-2">Como funciona</h3>
          <ul className="list-disc ml-5 text-sm text-gray-300 space-y-1">
            <li>Vendedor e comprador se conectam por um link da Safeswap</li>
            <li>O comprador envia o pagamento para a Safeswap (via Pix)</li>
            <li>O vendedor envia o ingresso</li>
            <li>O comprador confirma o recebimento no site</li>
            <li>A Safeswap libera o pagamento para o vendedor</li>
          </ul>
        </div>
        <div>
          <h3 className="text-lg font-bold mb-2">Por que usar a Safeswap?</h3>
          <ul className="list-disc ml-5 text-sm text-gray-300 space-y-1">
            <li>Sem golpes – nós seguramos o dinheiro com segurança</li>
            <li>Transferências rápidas e confiáveis</li>
            <li>Processo simples com menos etapas</li>
            <li>Suporte para qualquer ingresso de evento</li>
            <li>Integrado com Pix</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
