// src/FestaDetailPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function FestaDetailPage() {
  const navigate = useNavigate();
  const { state: { festa } = {} } = useLocation();
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setTimeout(() => setVisible(true), 100);
    window.scrollTo(0, 0);
  }, []);

  if (!festa) {
    navigate('/', { replace: true });
    return null;
  }

  const formatDate = (iso) => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  const mapSrc = `https://maps.google.com/maps?q=${encodeURIComponent(
    festa.endereco
  )}&t=&z=13&ie=UTF8&iwloc=&output=embed`;

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white font-sans px-4 py-8">
      <div className={`${visible ? 'opacity-100' : 'opacity-0'} transition-opacity duration-500`}>
        <button onClick={() => navigate(-1)} className="text-gray-300 hover:text-white mb-6">
          ← Voltar
        </button>

        {/* Banner Image */}
        {festa.imagem && (
          <img
            src={festa.imagem}
            alt={festa.nome}
            className="w-full h-64 object-cover rounded-lg mb-6"
          />
        )}

        {/* Title & Info */}
        <h1 className="text-3xl font-bold mb-2">{festa.nome}</h1>
        <p className="text-gray-400 mb-4">
          {formatDate(festa.data)} • {festa.local}
        </p>
        <p className="text-gray-200 mb-6">{festa.descricao}</p>

        {/* Address */}
        <h2 className="text-xl font-semibold mb-2">Endereço</h2>
        <p className="text-gray-200 mb-6">{festa.endereco}</p>

        {/* Map */}
        <div className="w-full h-64 mb-6">
          <iframe
            title="map"
            src={mapSrc}
            className="w-full h-full rounded-lg"
            allowFullScreen
            loading="lazy"
          ></iframe>
        </div>

        {/* Tickets */}
        <h2 className="text-xl font-semibold mb-4">Categorias de Ingressos</h2>
        <ul className="mb-6 space-y-2">
          {Object.entries(festa.categorias).map(([cat, preco]) => (
            <li key={cat} className="flex justify-between">
              <span>{cat}</span>
              <span className="font-bold">R$ {preco}</span>
            </li>
          ))}
        </ul>

        <button
          onClick={() => navigate('/checkout', { state: { festa } })}
          className="w-full bg-gradient-to-r from-indigo-500 to-teal-400 text-gray-900 font-bold py-3 rounded-lg"
        >
          Comprar Ingresso
        </button>
      </div>
    </div>
  );
}
