import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Checkout() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const festa = state?.festa;

  if (!festa) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
        <div className="bg-gray-800 p-6 rounded-lg">
          <p>Festa n√£o selecionada. Volte e escolha uma festa para comprar ingressos.</p>
          <button 
            onClick={() => navigate(-1)} 
            className="mt-4 px-4 py-2 bg-teal-500 text-gray-900 font-semibold rounded hover:bg-teal-400"
          >
            Voltar
          </button>
        </div>
      </div>
    );
  }

  const categorias = festa.categorias;
  const [quantidades, setQuantidades] = useState(
    Object.fromEntries(Object.keys(categorias).map(cat => [cat, 0]))
  );

  const handleChange = (cat, value) => {
    const v = Math.max(0, Math.min(5, Number(value)));
    setQuantidades(prev => ({ ...prev, [cat]: v }));
  };

  const handleQuantityChange = (cat, increment) => {
    const currentValue = quantidades[cat];
    const newValue = increment 
      ? Math.min(5, currentValue + 1) 
      : Math.max(0, currentValue - 1);
    setQuantidades(prev => ({ ...prev, [cat]: newValue }));
  };

  const total = Object.entries(quantidades)
    .reduce((sum, [cat, qty]) => sum + qty * categorias[cat], 0);

  const handleFinalize = () => {
    navigate('/confirmar', { state: { festaName: festa.nome, items: quantidades, total } });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white p-6">
      <button 
        onClick={() => navigate(-1)} 
        className="mb-6 text-lg hover:underline flex items-center"
      >
        <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
        </svg>
        Voltar
      </button>
      <h1 className="text-3xl font-bold mb-4">Ingressos: {festa.nome}</h1>
      <div className="bg-gray-700 rounded-lg p-6 max-w-md mx-auto space-y-4">
        {Object.entries(categorias).map(([cat, preco]) => (
          <div key={cat} className="flex justify-between items-center">
            <div>
              <p className="font-semibold">{cat}</p>
              <p className="text-gray-400 text-sm">R$ {preco.toFixed(2)}</p>
            </div>
            <div className="flex items-center">
              <button 
                onClick={() => handleQuantityChange(cat, false)}
                className="w-7 h-7 flex items-center justify-center bg-gray-600 text-white rounded-l"
              >
                <span className="text-lg font-bold pb-0.5">-</span>
              </button>
              <input
                type="number"
                min={0}
                max={5}
                value={quantidades[cat]}
                onChange={e => handleChange(cat, e.target.value)}
                className="w-10 bg-gray-600 text-white text-center border-l border-r border-gray-700"
              />
              <button 
                onClick={() => handleQuantityChange(cat, true)}
                className="w-7 h-7 flex items-center justify-center bg-gray-600 text-white rounded-r"
              >
                <span className="text-lg font-bold pb-0.5">+</span>
              </button>
            </div>
          </div>
        ))}
        <div className="border-t border-gray-600 pt-4 flex justify-between">
          <span className="font-semibold">Total</span>
          <span className="font-bold">R$ {total.toFixed(2)}</span>
        </div>
        <button
          onClick={handleFinalize}
          disabled={total === 0}
          className="w-full py-2 bg-teal-500 text-gray-900 font-bold rounded disabled:opacity-50 hover:bg-teal-400 transition-colors"
        >
          {total === 0 ? 'Selecione ingressos' : 'Finalizar Compra'}
        </button>
      </div>
    </div>
  );
}
