import { useLocation } from 'react-router-dom';

export default function Pagamento() {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const price = searchParams.get('valor');

  return (
    <main className="min-h-screen bg-gray-900 text-white font-sans">
      {/* Header com logo no canto esquerdo */}
      <header className="w-full p-6 shadow-md bg-gray-950 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src="/logo.png.jpg" alt="logo" className="h-10 w-10 rounded-xl" />
          <h1 className="text-2xl font-bold tracking-tight">Safeswap</h1>
        </div>
        <p className="text-sm text-gray-400">Seguro e rápido</p>
      </header>

      {/* Conteúdo principal */}
      <section className="flex flex-col items-center justify-center text-center px-4 py-10">
        <h1 className="text-3xl font-bold mb-4">Escolha sua forma de pagamento</h1>
        <p className="mb-6 text-lg">Valor: <strong>R$ {price}</strong></p>

        <div className="flex flex-col md:flex-row gap-6 items-start md:items-stretch">
          {/* QR Code para Pix */}
          <div className="bg-white p-4 rounded-xl text-black flex flex-col items-center w-64 min-h-[320px] justify-center">
            <p className="font-semibold mb-2">Pix (QR Code)</p>
            <img
              src="/pix_qrcode.png"
              alt="QR Code Pix"
              className="w-48 h-48 object-contain"
            />
          </div>

          {/* Pagamento por cartão de crédito */}
          <div className="bg-white p-4 rounded-xl text-black flex flex-col items-center w-64 min-h-[320px] justify-center">
            <p className="font-semibold mb-4">Cartão de Crédito</p>
            <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700">
              Pagar com cartão
            </button>
          </div>
        </div>
      </section>
    </main>
  );
}
