import { useState } from 'react';

export default function Safeswap() {
  const [role, setRole] = useState(null);
  const [started, setStarted] = useState(false);
  const [ticketName, setTicketName] = useState('');
  const [price, setPrice] = useState('');
  const [linkReady, setLinkReady] = useState(false);

  const handleBack = () => {
    if (linkReady) {
      setLinkReady(false);
    } else if (started) {
      setStarted(false);
    } else if (role) {
      setRole(null);
    }
  };

  const fakeLink = `http://localhost:3000/confirmar?ingresso=${encodeURIComponent(ticketName)}&valor=${encodeURIComponent(price)}&role=${role}`;

  const copyToClipboard = () => {
    navigator.clipboard.writeText(fakeLink);
    alert("Link copiado para a área de transferência!");
  };

  return (
    <main className="min-h-screen bg-gray-900 text-white font-sans">
      <header className="w-full p-6 shadow-md bg-gray-950">
        <div className="max-w-5xl mx-auto flex justify-between items-center">
          <div className="flex items-center gap-4">
            <img
              src="/logo.png.jpg"
              alt="Logo Safeswap"
              className="w-16 h-16 rounded-xl object-cover shadow-lg"
            />
            <h1 className="text-2xl font-bold tracking-tight">Safeswap</h1>
          </div>
          <p className="text-sm text-gray-400">Seguro e rápido</p>
        </div>
      </header>

      <section className="flex flex-col items-center justify-center text-center px-6 py-20">
        {(role || started || linkReady) && (
          <button
            className="absolute left-6 top-24 md:top-28 bg-gray-800 px-4 py-2 rounded-full text-sm text-white hover:bg-gray-700 transition"
            onClick={handleBack}
          >
            ⬅ Voltar
          </button>
        )}

        {/* Etapas iniciais */}
        {!role && !started && !linkReady && (
          <>
            <h2 className="text-4xl font-bold mb-4">Intermediação para Vendas de Ingressos Seguras</h2>
            <p className="text-lg text-gray-300 max-w-xl mb-6">
              Compradores e vendedores podem usar um único link para trocar ingressos com segurança.
              Nós seguramos o pagamento até que o comprador confirme o recebimento.
            </p>

            <div className="flex gap-4">
              <button
                className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-xl shadow-md"
                onClick={() => setRole('comprador')}
              >
                Sou Comprador
              </button>
              <button
                className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-xl shadow-md"
                onClick={() => setRole('vendedor')}
              >
                Sou Vendedor
              </button>
            </div>
          </>
        )}

        {/* Confirmação inicial */}
        {role && !started && !linkReady && (
          <div className="flex flex-col items-center gap-4 mt-6">
            <p className="text-gray-400 text-sm">Selecionado: <strong>{role}</strong></p>
            <button
              className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-2xl shadow-md"
              onClick={() => setStarted(true)}
            >
              Começar
            </button>
          </div>
        )}

        {/* Inputs */}
        {started && !linkReady && (
          <div className="flex flex-col items-center gap-6 mt-6 w-full max-w-md">
            <p className="text-gray-300 text-lg">
              Você está como <strong>{role}</strong>. Preencha os dados da transação:
            </p>

            <input
              type="text"
              placeholder="Nome do ingresso (ex: Lollapalooza)"
              className="w-full p-3 rounded-lg text-black"
              value={ticketName}
              onChange={(e) => setTicketName(e.target.value)}
            />

            <input
              type="number"
              placeholder="Preço combinado (R$)"
              className="w-full p-3 rounded-lg text-black"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />

            <button
              className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-xl shadow-md"
              onClick={() => setLinkReady(true)}
            >
              Confirmar
            </button>
          </div>
        )}

        {/* Link final */}
        {linkReady && (
          <div className="flex flex-col items-center gap-6 w-full max-w-lg">
            <p className="text-xl font-semibold">
              {role === 'comprador'
                ? 'Copie esse link e envie para o vendedor!'
                : 'Copie esse link e envie para o comprador!'}
            </p>

            <div className="bg-gray-800 p-4 rounded-lg w-full text-left break-words text-sm text-gray-300">
              {fakeLink}
            </div>

            <button
              onClick={copyToClipboard}
              className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-xl shadow-md"
            >
              Copiar link
            </button>
          </div>
        )}
      </section>

      {/* Seção explicativa do site */}
      <section id="como-funciona" className="bg-gray-800 py-16 px-6">
        <div className="max-w-4xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12">
          <div>
            <h3 className="text-2xl font-semibold mb-4">Como funciona</h3>
            <ul className="list-disc list-inside space-y-2 text-gray-300">
              <li>Vendedor e comprador se conectam por um link da Safeswap</li>
              <li>O comprador envia o pagamento para a Safeswap (via Pix)</li>
              <li>O vendedor envia o ingresso</li>
              <li>O comprador confirma o recebimento no site</li>
              <li>A Safeswap libera o pagamento para o vendedor</li>
            </ul>
          </div>
          <div>
            <h3 className="text-2xl font-semibold mb-4">Por que usar a Safeswap?</h3>
            <ul className="list-disc list-inside space-y-2 text-gray-300">
              <li>Sem golpes – nós seguramos o dinheiro com segurança</li>
              <li>Transferências rápidas e confiáveis</li>
              <li>Processo simples com menos etapas</li>
              <li>Suporte para qualquer ingresso de evento</li>
              <li>Integrado com Pix para transações instantâneas</li>
            </ul>
          </div>
        </div>
      </section>
    </main>
  );
}
