import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const navigate = useNavigate();

  const dateToStr = (date) => date.toISOString().split('T')[0];

  // Carrega e ordena festas
  useEffect(() => {
    const ordenadas = festasJson.sort(
      (a, b) => new Date(a.data) - new Date(b.data)
    );
    setFestas(ordenadas);
  }, []);

  // Filtros
  const festasFiltradas = festas
    .filter((f) => (dataSelecionada ? f.data === dateToStr(dataSelecionada) : true))
    .filter((f) => (searchTerm ? f.nome.toLowerCase().includes(searchTerm.toLowerCase()) : true));

  const tileClassName = ({ date }) => {
    const dateStr = dateToStr(date);
    return festas.some((f) => f.data === dateStr) ? 'highlight-date' : null;
  };

  // Ao clicar, navega para página de detalhe
  const handleCardClick = (festa) => {
    // Aqui você pode trocar para o caminho que preferir, ex: /festa/:id
    navigate(`/festa/${encodeURIComponent(festa.nome)}`, { state: festa });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white p-6 font-sans">
      <style>{`
        .react-calendar { background-color: rgba(255,255,255,0.1); color: #e0f2f1; border-radius: 1rem; border: none; padding: 1rem; }
        .react-calendar__tile { padding: 0.75rem 0.5rem; border-radius: 0.5rem; transition: background 0.2s ease; }
        .react-calendar__tile--now { background: rgba(61, 218, 215, 0.2); }
        .react-calendar__tile--active { background: #3ddad7 !important; color: #0f172a !important; }
        .highlight-date { background-color: #3ddad7 !important; color: #0f172a !important; font-weight: bold; }
      `}</style>

      {/* Header e busca */}
      <header className="flex justify-between items-center mb-8">
        <h1 className="flex items-baseline space-x-3 text-4xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-teal-300">
          <span className="cursor-pointer hover:underline" onClick={() => navigate('/')}>←</span>
          <span>Festas Disponíveis</span>
        </h1>
        <input
          type="text"
          placeholder="🔍 Pesquisar festa..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full max-w-xs bg-white bg-opacity-10 placeholder-gray-400 text-white px-4 py-2 rounded-full focus:outline-none"
        />
      </header>

      {/* Filtro de data */}
      <div className="text-center mb-10">
        <button
          onClick={() => setMostrarCalendario(!mostrarCalendario)}
          className="inline-flex items-center space-x-2 bg-white bg-opacity-10 hover:bg-opacity-20 px-5 py-2 rounded-full font-medium transition-all"
        >
          <span role="img" aria-label="calendar">📅</span>
          <span>{mostrarCalendario ? 'Esconder calendário' : 'Filtrar por data'}</span>
        </button>
        {mostrarCalendario && (
          <div className="mt-6 max-w-sm mx-auto">
            <Calendar
              onChange={(date) => { setDataSelecionada(date); setMostrarCalendario(false); }}
              value={dataSelecionada}
              tileClassName={tileClassName}
            />
            {dataSelecionada && (
              <button onClick={() => setDataSelecionada(null)} className="mt-3 text-sm underline">
                Ver todas as festas
              </button>
            )}
          </div>
        )}
      </div>

      {/* Lista de festas como cards clicáveis */}
      <ul className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
        {festasFiltradas.length === 0 ? (
          <li className="col-span-full text-center text-gray-400 py-20">
            Nenhuma festa encontrada.
          </li>
        ) : (
          festasFiltradas.map((festa) => (
            <li
              key={festa.nome}
              onClick={() => handleCardClick(festa)}
              className="cursor-pointer bg-white bg-opacity-10 backdrop-blur-md rounded-2xl p-6 shadow-lg hover:shadow-2xl transform hover:-translate-y-1 transition-all"
            >
              {festa.imagem && (
                <img src={festa.imagem} alt={festa.nome} className="w-full h-48 object-cover rounded-xl mb-4" />
              )}
              <div className="flex justify-between items-center mb-2">
                <h3 className="text-xl font-semibold bg-clip-text text-transparent bg-gradient-to-r from-indigo-300 to-teal-200">
                  {festa.nome}
                </h3>
                <span className="text-xs bg-gradient-to-r from-[#3ddad7] to-[#58e6dc] text-[#0f172a] px-3 py-1 rounded-full font-bold">
                  {new Date(festa.data).toLocaleDateString('pt-BR')}
                </span>
              </div>
              {festa.descricao && <p className="text-sm text-gray-200 mb-4">{festa.descricao}</p>}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}
