// src/RevendaPage.js
import { Routes, Route, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import LinkPage from './LinkPage';
import ConfirmPage from './ConfirmPage';

export default function RevendaPage() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [ticketName, setTicketName] = useState('');
  const [price, setPrice] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [showLink, setShowLink] = useState(false);

  const handleGenerateLink = () => {
    if (ticketName && price) {
      setShowLink(true);
    } else {
      alert('Preencha todos os campos antes de continuar.');
    }
  };

  const handleBackFromLink = () => {
    setShowLink(false);
  };

  const howItWorks = [
    "Vendedor e comprador se conectam por um link da Safeswap",
    "O comprador envia o pagamento para a Safeswap (via Pix)",
    "O vendedor envia o ingresso",
    "O comprador confirma o recebimento no site",
    "A Safeswap libera o pagamento para o vendedor"
  ];

  return (
    <Routes>
      <Route
        path="/"
        element={
          <main className="min-h-screen bg-[#0f172a] text-[#e0f2f1] font-sans">
            <header className="w-full p-6 shadow-md bg-[#1e293b] flex justify-between items-center">
              <div className="flex items-center gap-3">
                <img src="/logo.png.jpg" alt="logo" className="h-10 w-10 rounded-xl" />
                <h1 className="text-2xl font-bold tracking-tight text-[#3ddad7]">Safeswap</h1>
              </div>
              <p className="text-sm text-[#94a3b8]">Seguro e rápido</p>
            </header>

            <section className="flex flex-col items-center justify-center text-center px-6 py-16">
              {/* Seta só aparece na primeira etapa */}
              {!role && !showForm && (
                <button
                  onClick={() => navigate('/')}
                  className="self-start text-[#94a3b8] hover:text-white text-sm mb-6"
                >
                  ← Voltar
                </button>
              )}

              {!role && !showForm ? (
                <>
                  <h2 className="text-4xl font-bold mb-6 text-white">
                    Intermediação de revendas de Ingressos
                  </h2>
                  <p className="text-lg text-[#cbd5e1] max-w-xl mb-8">
                    Compre ingressos de forma segura e rápida, sem se preocupar com fraudes ou problemas de pagamento.
                  </p>

                  {/* Como funciona - Centralizado */}
                  <div className="mb-10 w-full max-w-2xl mx-auto">
                    <h3 className="text-2xl font-semibold mb-4 text-[#67e8f9]">Como funciona</h3>
                    <div className="bg-[#1e293b] p-6 rounded-xl shadow-lg">
                      <ol className="text-left text-[#cbd5e1] flex flex-col gap-3">
                        {howItWorks.map((step, index) => (
                          <li key={index} className="flex items-start gap-2">
                            <span className="bg-[#3ddad7] text-[#0f172a] w-6 h-6 rounded-full flex items-center justify-center flex-shrink-0 font-bold">
                              {index + 1}
                            </span>
                            <span>{step}</span>
                          </li>
                        ))}
                      </ol>

                      {/* Botão de copiar */}
                      <button 
                        onClick={() => {
                          navigator.clipboard.writeText(howItWorks.map((step, i) => `${i+1}. ${step}`).join('\n'));
                          alert('Informações copiadas com sucesso!');
                        }}
                        className="mt-4 px-4 py-2 bg-[#334155] hover:bg-[#475569] text-white rounded-lg flex items-center gap-2 mx-auto"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                          <rect x="9" y="9" width="13" height="13" rx="2" ry="2"></rect>
                          <path d="M5 15H4a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2h9a2 2 0 0 1 2 2v1"></path>
                        </svg>
                        Copiar informações
                      </button>
                    </div>
                  </div>

                  <div className="flex gap-4">
                    <button
                      onClick={() => {
                        setRole('comprador');
                        setShowForm(true);
                      }}
                      className="bg-[#3b82f6] hover:bg-[#2563eb] px-6 py-2 rounded-xl text-white font-medium transition"
                    >
                      Sou Comprador
                    </button>
                    <button
                      onClick={() => {
                        setRole('vendedor');
                        setShowForm(true);
                      }}
                      className="bg-[#10b981] hover:bg-[#059669] px-6 py-2 rounded-xl text-white font-medium transition"
                    >
                      Sou Vendedor
                    </button>
                  </div>
                </>
              ) : showForm && !showLink ? (
                <>
                  <button
                    onClick={() => {
                      setRole(null);
                      setShowForm(false);
                    }}
                    className="text-sm text-[#94a3b8] mb-4 self-start"
                  >
                    ← Voltar
                  </button>
                  <h2 className="text-2xl font-semibold mb-4 text-white">
                    Preencha as informações do ingresso
                  </h2>
                  <div className="w-full max-w-md">
                    <input
                      placeholder="Nome do ingresso"
                      className="mb-4 px-4 py-2 rounded text-gray-900 w-full"
                      value={ticketName}
                      onChange={(e) => setTicketName(e.target.value)}
                    />
                    <input
                      type="number"
                      placeholder="Preço combinado (R$)"
                      className="mb-4 px-4 py-2 rounded text-gray-900 w-full"
                      value={price}
                      onChange={(e) => setPrice(e.target.value)}
                      min="0"
                      step="0.50"
                    />
                    <button
                      className="bg-[#3ddad7] hover:bg-[#2ec4b6] text-black font-semibold px-6 py-2 rounded-xl shadow w-full"
                      onClick={handleGenerateLink}
                    >
                      Gerar link
                    </button>
                  </div>
                </>
              ) : (
                <>
                  <button onClick={handleBackFromLink} className="text-sm text-[#94a3b8] mb-4 self-start">
                    ← Voltar
                  </button>
                  <LinkPage
                    ticketName={ticketName}
                    price={price}
                    role={role}
                    setStarted={setShowLink}
                    setRole={setRole}
                    setTicketName={setTicketName}
                    setPrice={setPrice}
                  />
                </>
              )}
            </section>
          </main>
        }
      />
      <Route path="/confirmar" element={<ConfirmPage />} />
    </Routes>
  );
}
