// src/FestasPage.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [openIndex, setOpenIndex] = useState(null);
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  // Ordena festas ao montar
  useEffect(() => {
    const ordenadas = [...festasJson].sort(
      (a, b) => new Date(a.data) - new Date(b.data)
    );
    setFestas(ordenadas);
  }, []);

  // Fecha card aberto ao mudar filtros ou busca
  useEffect(() => {
    setOpenIndex(null);
  }, [dataSelecionada, searchTerm]);

  // Formata Date para "YYYY-MM-DD", usando local
  const dateToStrLocal = (date) => {
    const y = date.getFullYear();
    const m = String(date.getMonth() + 1).padStart(2, '0');
    const d = String(date.getDate()).padStart(2, '0');
    return `${y}-${m}-${d}`;
  };

  // Filtra por data e nome
  const festasFiltradas = festas
    .filter((f) => (dataSelecionada ? f.data === dateToStrLocal(dataSelecionada) : true))
    .filter((f) => (searchTerm ? f.nome.toLowerCase().includes(searchTerm.toLowerCase()) : true));

  const tileClassName = ({ date }) => {
    const dateStr = dateToStrLocal(date);
    return festas.some((f) => f.data === dateStr) ? 'highlight-date' : null;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white p-6 font-sans">
      {/* Styles */}
      <style>{`
        .react-calendar { background-color: rgba(255,255,255,0.1); color: #e0f2f1; border-radius: 1rem; border: none; padding: 1rem; }
        .react-calendar__tile { padding: 0.75rem 0.5rem; border-radius: 0.5rem; transition: background 0.2s ease; }
        .react-calendar__tile--now { background: rgba(61, 218, 215, 0.2); }
        .react-calendar__tile--active { background: #3ddad7 !important; color: #0f172a !important; }
        .highlight-date { background-color: #3ddad7 !important; color: #0f172a !important; font-weight: bold; }
      `}</style>

      {/* Header */}
      <header className="flex justify-between items-center mb-8">
        <h1 className="flex items-center space-x-3 text-4xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-teal-300">
          <span className="cursor-pointer" onClick={() => navigate('/')}>â</span>
          <span>Festas DisponÃ­veis</span>
        </h1>
        <input
          type="text"
          placeholder="ð Pesquisar festa..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full max-w-xs bg-white bg-opacity-10 placeholder-gray-400 text-white px-4 py-2 rounded-full"
        />
      </header>

      {/* CalendÃ¡rio */}
      <div className="text-center mb-10">
        <button
          onClick={() => setMostrarCalendario(!mostrarCalendario)}
          className="inline-flex items-center space-x-2 bg-white bg-opacity-10 hover:bg-opacity-20 px-5 py-2 rounded-full font-medium"
        >
          ð <span>{mostrarCalendario ? 'Esconder calendÃ¡rio' : 'Filtrar por data'}</span>
        </button>
        {mostrarCalendario && (
          <div className="mt-6 max-w-sm mx-auto">
            <Calendar
              onChange={setDataSelecionada}
              value={dataSelecionada}
              tileClassName={tileClassName}
            />
            {dataSelecionada && (
              <button onClick={() => setDataSelecionada(null)} className="mt-3 text-sm underline">
                Ver todas as festas
              </button>
            )}
          </div>
        )}
      </div>

      {/* Lista de festas */}
      <ul className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
        {festasFiltradas.length === 0 ? (
          <li className="col-span-full text-center text-gray-400 py-20">Nenhuma festa encontrada.</li>
        ) : (
          festasFiltradas.map((f, idx) => {
            const isOpen = openIndex === idx;
            // formata data de f.data (YYYY-MM-DD) para DD/MM/YYYY
            const [year, month, day] = f.data.split('-');
            const displayDate = `${day}/${month}/${year}`;

            return (
              <li
                key={f.nome + idx}
                className={`bg-white bg-opacity-10 backdrop-blur-md rounded-2xl shadow-lg hover:shadow-2xl transform hover:-translate-y-1 transition-all ${isOpen ? 'p-6' : 'py-4 px-6'}`}
              >
                <button
                  onClick={() => setOpenIndex(isOpen ? null : idx)}
                  className="w-full flex justify-between items-center text-left"
                >
                  <h3 className="text-xl font-semibold bg-clip-text text-transparent bg-gradient-to-r from-indigo-300 to-teal-200">
                    {f.nome}
                  </h3>
                  <span className="text-xs bg-gradient-to-r from-[#3ddad7] to-[#58e6dc] text-[#0f172a] px-3 py-1 rounded-full font-bold">
                    {displayDate}
                  </span>
                </button>

                {isOpen && (
                  <div className="mt-4 space-y-4">
                    {f.imagem && <img src={f.imagem} alt={f.nome} className="w-full h-48 object-cover rounded-xl" />}
                    {f.descricao && <p className="text-sm text-gray-200">{f.descricao}</p>}
                    {f.categorias && (
                      <div>
                        <h4 className="text-sm font-semibold mb-2">Categorias de Ingressos</h4>
                        <ul className="space-y-2">
                          {Object.entries(f.categorias).map(([cat, preco]) => (
                            <li key={cat} className="flex justify-between">
                              <span>{cat}</span>
                              <span className="font-semibold">R$ {preco}</span>
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                    <button className="w-full bg-gradient-to-r from-indigo-400 to-teal-300 text-[#0f172a] font-bold py-2 rounded-full">
                      ð« Comprar Ingresso
                    </button>
                  </div>
                )}
              </li>
            );
          })
        )}
      </ul>
    </div>
  );
}
