import React, { useEffect, useState, Fragment } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';
import { Dialog, Transition } from '@headlessui/react';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [isCalendarOpen, setIsCalendarOpen] = useState(false);
  const [modalFesta, setModalFesta] = useState(null);

  useEffect(() => {
    // deep copy + sort
    const sorted = [...festasJson].sort((a, b) => new Date(a.data) - new Date(b.data));
    setFestas(sorted);
  }, []);

  const dateToStr = date => date.toISOString().split('T')[0];

  const filtered = festas
    .filter(f => (dataSelecionada ? f.data === dateToStr(dataSelecionada) : true))
    .filter(f => f.nome.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white p-8">
      {/* Search & Calendar */}
      <div className="flex flex-col md:flex-row items-center justify-between mb-8 space-y-4 md:space-y-0">
        <input
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          placeholder="Buscar festa..."
          className="w-full md:w-1/3 px-4 py-2 rounded-lg bg-gray-700 placeholder-gray-400 text-white focus:outline-none"
        />
        <button
          onClick={() => setIsCalendarOpen(true)}
          className="px-4 py-2 bg-gradient-to-r from-teal-400 to-blue-500 rounded-lg shadow hover:from-teal-300 hover:to-blue-400 transition"
        >
          Filtrar por data
        </button>
      </div>

      {/* Grid of Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {filtered.map((festa, i) => {
          const [year, month, day] = festa.data.split('-');
          return (
            <div
              key={festa.nome + i}
              className="bg-gray-700 rounded-2xl overflow-hidden shadow-lg cursor-pointer hover:shadow-2xl transition"
              onClick={() => setModalFesta(festa)}
            >
              {festa.imagem && (
                <div className="h-40 bg-cover bg-center" style={{ backgroundImage: `url(${festa.imagem})` }} />
              )}
              <div className="p-4">
                <h3 className="text-xl font-bold mb-1">{festa.nome}</h3>
                <p className="text-sm text-gray-300 mb-2">
                  {day}/{month}/{year}
                </p>
                <p className="text-sm text-gray-400 truncate">{festa.descricao}</p>
              </div>
            </div>
          );
        })}
        {filtered.length === 0 && (
          <p className="col-span-full text-center text-gray-500">Nenhuma festa encontrada.</p>
        )}
      </div>

      {/* Calendar Modal */}
      <Transition appear show={isCalendarOpen} as={Fragment}>
        <Dialog as="div" className="fixed inset-0 z-10 overflow-y-auto" onClose={() => setIsCalendarOpen(false)}>
          <div className="min-h-screen px-4 text-center">
            <Dialog.Overlay className="fixed inset-0 bg-black opacity-50" />
            <span className="inline-block h-screen align-middle" aria-hidden="true">\u200B</span>
            <div className="inline-block w-full max-w-md p-6 my-8 overflow-hidden text-left align-middle transition-transform bg-gray-900 rounded-2xl shadow-xl">
              <Dialog.Title className="text-lg font-medium text-white mb-4">Selecione uma data</Dialog.Title>
              <Calendar
                onChange={date => {
                  setDataSelecionada(date);
                  setIsCalendarOpen(false);
                }}
                value={dataSelecionada}
              />
              <button
                onClick={() => setIsCalendarOpen(false)}
                className="mt-4 px-4 py-2 bg-gray-700 rounded-lg hover:bg-gray-600"
              >Fechar</button>
            </div>
          </div>
        </Dialog>
      </Transition>

      {/* Festa Detail Modal */}
      <Transition appear show={!!modalFesta} as={Fragment}>
        <Dialog as="div" className="fixed inset-0 z-20 overflow-y-auto" onClose={() => setModalFesta(null)}>
          <div className="min-h-screen px-4 text-center">
            <Dialog.Overlay className="fixed inset-0 bg-black opacity-70" />
            <span className="inline-block h-screen align-middle" aria-hidden="true">\u200B</span>
            <div className="inline-block w-full max-w-lg p-6 my-8 overflow-hidden text-left align-middle bg-gray-800 rounded-2xl shadow-xl">
              <Dialog.Title className="text-2xl font-bold text-white mb-2">{modalFesta?.nome}</Dialog.Title>
              <p className="text-sm text-gray-400 mb-4">{modalFesta && modalFesta.data.split('-').reverse().join('/')}</p>
              {modalFesta?.imagem && (
                <img src={modalFesta.imagem} alt="" className="w-full h-48 object-cover rounded-lg mb-4" />
              )}
              <p className="text-gray-200 mb-4">{modalFesta?.descricao}</p>
              {modalFesta?.categorias && (
                <div className="mb-4">
                  <h4 className="text-white font-semibold mb-2">Categorias de Ingressos</h4>
                  <ul>
                    {Object.entries(modalFesta.categorias).map(([cat, preco]) => (
                      <li key={cat} className="flex justify-between text-gray-200">
                        <span>{cat}</span><span>R$ {preco}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
              <button
                onClick={() => alert('Comprar ingressos...')}
                className="w-full py-2 bg-gradient-to-r from-teal-400 to-blue-500 rounded-lg text-white font-bold"
              >Comprar Ingresso</button>
            </div>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
}
