// src/FestaDetailPage.js
import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function FestaDetailPage() {
  const navigate = useNavigate();
  const { state: { festa } = {} } = useLocation();

  if (!festa) {
    navigate('/', { replace: true });
    return null;
  }

  const displayDate = (iso) => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] p-8 font-sans text-white flex items-center justify-center">
      <div className="bg-slate-800 bg-opacity-50 backdrop-blur-lg rounded-2xl shadow-xl overflow-hidden w-full max-w-5xl grid grid-cols-1 md:grid-cols-2">
        {/* Imagem do Evento */}
        {festa.imagem && (
          <div className="h-64 md:h-auto overflow-hidden">
            <img
              src={festa.imagem}
              alt={festa.nome}
              className="w-full h-full object-cover"
            />
          </div>
        )}

        {/* Conteúdo */}
        <div className="p-8 flex flex-col justify-between space-y-6">
          <div>
            <button
              onClick={() => navigate(-1)}
              className="text-sm text-gray-300 hover:text-white mb-4 flex items-center"
            >
              ← Voltar
            </button>

            <h1 className="text-3xl md:text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-indigo-400 to-teal-300">
              {festa.nome}
            </h1>

            <div className="mt-4 flex flex-wrap items-center gap-6 text-gray-300">
              <div className="flex items-center space-x-2">
                <span>📅</span>
                <span>{displayDate(festa.data)}</span>
              </div>
              <div className="flex items-center space-x-2">
                <span>📍</span>
                <span>{festa.local}</span>
              </div>
            </div>

            <p className="mt-6 text-gray-200 leading-relaxed">
              {festa.descricao}
            </p>
          </div>

          <div>
            <h2 className="text-2xl font-semibold mb-4">Categorias de Ingressos</h2>
            <ul className="grid grid-cols-1 gap-3">
              {Object.entries(festa.categorias).map(([cat, preco]) => (
                <li
                  key={cat}
                  className="flex justify-between items-center bg-slate-700 bg-opacity-40 rounded-lg px-4 py-2"
                >
                  <span className="font-medium">{cat}</span>
                  <span className="font-bold">R$ {preco}</span>
                </li>
              ))}
            </ul>

            <button
              onClick={() => navigate('/checkout', { state: { festa } })}
              className="mt-8 w-full md:w-auto px-6 py-3 bg-gradient-to-r from-indigo-500 to-teal-400 text-gray-900 font-bold rounded-full shadow-lg hover:scale-105 transform transition"
            >
              🎫 Comprar Ingresso
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
