// src/FestaDetailPage.js
import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function FestaDetailPage() {
  const navigate = useNavigate();
  const { state: { festa } = {} } = useLocation();
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    // Animation effect on component mount
    setIsLoaded(true);
    
    // Scroll to top on page load
    window.scrollTo(0, 0);
  }, []);

  if (!festa) {
    navigate('/', { replace: true });
    return null;
  }

  const displayDate = (iso) => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-slate-900 to-indigo-950 px-4 sm:px-6 md:px-12 py-16 text-white font-sans">
      {/* Header with Back Button */}
      <div className={`transform transition-all duration-500 ${isLoaded ? 'translate-y-0 opacity-100' : '-translate-y-10 opacity-0'}`}>
        <button
          onClick={() => navigate(-1)}
          className="flex items-center gap-2 text-gray-300 hover:text-white mb-6 group transition-all duration-300"
        >
          <span className="text-lg transform group-hover:-translate-x-1 transition-transform duration-300">‚Üê</span>
          <span className="text-lg font-medium">Voltar</span>
        </button>
      </div>

      {/* Main Content Card */}
      <div className={`flex flex-col lg:flex-row w-full rounded-3xl overflow-hidden shadow-2xl transform transition-all duration-700 ${isLoaded ? 'translate-y-0 opacity-100 scale-100' : 'translate-y-10 opacity-0 scale-95'}`}>
        {/* Image Section with Overlay */}
        {festa.imagem && (
          <div className="w-full lg:w-1/2 relative h-96 lg:h-auto overflow-hidden">
            <div className="absolute inset-0 bg-gradient-to-t from-black to-transparent opacity-40 z-10"></div>
            <img
              src={festa.imagem}
              alt={festa.nome}
              className="w-full h-full object-cover transition-transform duration-10000 hover:scale-105"
            />
            
            {/* Floating date badge on the image */}
            <div className="absolute top-6 right-6 bg-black bg-opacity-70 backdrop-blur-md px-4 py-2 rounded-full z-20 flex items-center gap-2">
              <span className="text-indigo-300">üìÖ</span>
              <span className="font-medium">{displayDate(festa.data)}</span>
            </div>
          </div>
        )}

        {/* Evento Details */}
        <div className="w-full lg:w-1/2 p-8 md:p-12 space-y-8 bg-slate-900 bg-opacity-95 backdrop-blur-xl">
          {/* Event Title with Animated Gradient */}
          <h1 className="text-4xl lg:text-5xl font-extrabold animate-text bg-gradient-to-r from-teal-300 via-purple-400 to-indigo-300 bg-clip-text text-transparent">
            {festa.nome}
          </h1>

          {/* Event Details Row */}
          <div className="flex flex-col sm:flex-row gap-6 text-gray-200 text-lg">
            <div className="flex items-center space-x-3 bg-slate-800 bg-opacity-40 px-4 py-2 rounded-lg">
              <span className="text-indigo-300">üìç</span>
              <span>{festa.local}</span>
            </div>
          </div>

          {/* Event Description */}
          <div className="bg-slate-800 bg-opacity-40 p-6 rounded-xl">
            <h2 className="text-xl font-semibold mb-3 text-indigo-200">Sobre o Evento</h2>
            <p className="text-gray-200 leading-relaxed">
              {festa.descricao}
            </p>
          </div>

          {/* Ticket Categories */}
          <div>
            <h2 className="text-2xl font-semibold mb-4 text-indigo-200 flex items-center gap-2">
              <span className="text-indigo-300">üé´</span> Categorias de Ingressos
            </h2>
            <ul className="space-y-3">
              {Object.entries(festa.categorias).map(([cat, preco], index) => (
                <li
                  key={cat}
                  className={`flex justify-between items-center rounded-xl px-6 py-4 transform transition-all duration-300 hover:translate-x-1 hover:shadow-lg ${
                    index % 2 === 0 ? 'bg-indigo-900 bg-opacity-30' : 'bg-slate-800 bg-opacity-50'
                  }`}
                  style={{ 
                    transitionDelay: `${index * 50}ms`,
                    animationDelay: `${index * 100}ms`
                  }}
                >
                  <span className="font-medium text-lg">{cat}</span>
                  <span className="font-bold text-lg bg-indigo-900 bg-opacity-70 px-4 py-1 rounded-full">
                    R$ {preco}
                  </span>
                </li>
              ))}
            </ul>
          </div>

          {/* CTA Button with Animation */}
          <button
            onClick={() => navigate('/checkout', { state: { festa } })}
            className="w-full mt-8 py-4 bg-gradient-to-r from-indigo-600 to-purple-500 text-white font-bold text-xl rounded-xl shadow-xl transition-all duration-300 hover:shadow-indigo-500/30 hover:shadow-2xl hover:scale-[1.02] flex items-center justify-center gap-2"
          >
            <span className="text-xl">üéüÔ∏è</span>
            <span>Comprar Ingresso</span>
          </button>
        </div>
      </div>
      
      {/* Decorative Elements */}
      <div className="fixed top-0 left-0 w-full h-screen overflow-hidden pointer-events-none z-0">
        <div className="absolute -top-24 -left-24 w-64 h-64 bg-indigo-600 opacity-10 rounded-full blur-3xl"></div>
        <div className="absolute top-1/3 -right-32 w-96 h-96 bg-purple-600 opacity-10 rounded-full blur-3xl"></div>
      </div>
    </div>
  );
}
