// src/FestaDetailPage.js
import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function FestaDetailPage() {
  const navigate = useNavigate();
  const { state: { festa } = {} } = useLocation();

  if (!festa) {
    navigate('/', { replace: true });
    return null;
  }

  const displayDate = (iso) => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white font-sans px-8 py-12">
      {/* Bot√£o de voltar */}
      <button
        onClick={() => navigate(-1)}
        className="text-lg text-gray-300 hover:text-white mb-6"
      >
        ‚Üê Voltar
      </button>

      {/* Imagem em full width */}
      {festa.imagem && (
        <img
          src={festa.imagem}
          alt={festa.nome}
          className="w-full h-80 object-cover mb-8"
        />
      )}

      {/* T√≠tulo e informa√ß√µes */}
      <h1 className="text-4xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-teal-300 mb-4">
        {festa.nome}
      </h1>
      <div className="flex flex-wrap text-gray-300 text-lg mb-6 gap-6">
        <div className="flex items-center space-x-2">
          <span>üìÖ</span>
          <span>{displayDate(festa.data)}</span>
        </div>
        <div className="flex items-center space-x-2">
          <span>üìç</span>
          <span>{festa.local}</span>
        </div>
      </div>

      {/* Descri√ß√£o */}
      <p className="text-gray-200 leading-relaxed mb-8">
        {festa.descricao}
      </p>

      {/* Categorias */}
      <h2 className="text-2xl font-semibold mb-4">Categorias de Ingressos</h2>
      <ul className="mb-8">
        {Object.entries(festa.categorias).map(([cat, preco]) => (
          <li key={cat} className="flex justify-between mb-2">
            <span>{cat}</span>
            <span className="font-bold">R$ {preco}</span>
          </li>
        ))}
      </ul>

      {/* Bot√£o de compra */}
      <button
        onClick={() => navigate('/checkout', { state: { festa } })}
        className="w-full py-4 bg-gradient-to-r from-indigo-500 to-teal-400 text-gray-900 font-bold text-lg rounded-full transition hover:opacity-90"
      >
        üé´ Comprar Ingresso
      </button>
    </div>
  );
}
