import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { Calendar as CalendarIcon, Ticket } from 'lucide-react';
import { motion } from 'framer-motion';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [festaAberta, setFestaAberta] = useState(null);
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const navigate = useNavigate();

  const dateToStr = (date) => date.toISOString().split('T')[0];

  useEffect(() => {
    const ordenadas = festasJson.sort(
      (a, b) => new Date(a.data) - new Date(b.data)
    );
    setFestas(ordenadas);
  }, []);

  const handleDateChange = (date) => {
    setDataSelecionada(date);
    setFestaAberta(null);
    setMostrarCalendario(false);
  };

  const festasFiltradas = dataSelecionada
    ? festas.filter((f) => f.data === dateToStr(dataSelecionada))
    : festas;

  const tileClassName = ({ date }) => {
    const dateStr = dateToStr(date);
    const existeFesta = festas.some((f) => f.data === dateStr);
    return existeFesta ? 'highlight-date' : null;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white p-6 font-sans">
      {/* Custom Calendar Styles */}
      <style>{`
        .react-calendar {
          background-color: rgba(255,255,255,0.1);
          color: #e0f2f1;
          border-radius: 1rem;
          border: none;
          padding: 1rem;
        }
        .react-calendar__tile {
          padding: 0.75rem 0.5rem;
          border-radius: 0.5rem;
          transition: background 0.2s ease;
        }
        .react-calendar__tile--now {
          background: rgba(61, 218, 215, 0.2);
        }
        .react-calendar__tile--active {
          background: #3ddad7 !important;
          color: #0f172a !important;
        }
        .highlight-date {
          background-color: #3ddad7 !important;
          color: #0f172a !important;
          font-weight: bold;
        }
      `}</style>

      {/* Header */}
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-teal-300">
          Festas Disponíveis
        </h1>
        <button
          onClick={() => navigate('/')}
          className="text-sm flex items-center space-x-1 hover:underline"
        >
          <CalendarIcon size={16} />
          <span>Voltar ao início</span>
        </button>
      </header>

      {/* Calendar Filter */}
      <div className="text-center mb-10">
        <button
          onClick={() => setMostrarCalendario(!mostrarCalendario)}
          className="inline-flex items-center space-x-2 bg-white bg-opacity-10 hover:bg-opacity-20 px-5 py-2 rounded-full font-medium transition-all"
        >
          <CalendarIcon size={18} />
          <span>{mostrarCalendario ? 'Esconder calendário' : 'Filtrar por data'}</span>
        </button>
        {mostrarCalendario && (
          <div className="mt-6 max-w-sm mx-auto">
            <Calendar
              onChange={handleDateChange}
              value={dataSelecionada}
              tileClassName={tileClassName}
            />
            {dataSelecionada && (
              <button
                onClick={() => setDataSelecionada(null)}
                className="mt-3 text-sm underline"
              >
                Ver todas as festas
              </button>
            )}
          </div>
        )}
      </div>

      {/* Festas List */}
      <ul className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
        {festasFiltradas.length === 0 ? (
          <li className="col-span-full text-center text-gray-400 py-20">
            Nenhuma festa disponível nesta data.
          </li>
        ) : (
          festasFiltradas.map((festa, index) => (
            <motion.li
              key={index}
              layout
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
              className="bg-white bg-opacity-10 backdrop-blur-md rounded-2xl p-6 shadow-lg hover:shadow-2xl transform hover:-translate-y-1 transition-all"
            >
              <button
                onClick={() => setFestaAberta(festaAberta === index ? null : index)}
                className="w-full flex justify-between items-center text-left"
              >
                <h3 className="text-xl font-semibold bg-clip-text text-transparent bg-gradient-to-r from-indigo-300 to-teal-200">
                  {festa.nome}
                </h3>
                <span className="text-xs bg-gradient-to-r from-[#3ddad7] to-[#58e6dc] text-[#0f172a] px-3 py-1 rounded-full font-bold">
                  {new Date(festa.data).toLocaleDateString('pt-BR')}
                </span>
              </button>

              {festaAberta === index && (
                <div className="mt-4 space-y-4">
                  {festa.imagem && (
                    <img
                      src={festa.imagem}
                      alt={festa.nome}
                      className="w-full h-48 object-cover rounded-xl"
                    />
                  )}
                  {festa.descricao && (
                    <p className="text-sm text-gray-200">{festa.descricao}</p>
                  )}
                  {festa.categorias && (
                    <div>
                      <h4 className="text-sm font-semibold mb-2">Categorias de Ingressos</h4>
                      <ul className="space-y-2">
                        {Object.entries(festa.categorias).map(([cat, preco], i) => (
                          <li key={i} className="flex justify-between">
                            <span>{cat}</span>
                            <span className="font-semibold">R$ {preco}</span>
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                  <button className="w-full flex items-center justify-center space-x-2 bg-gradient-to-r from-indigo-400 to-teal-300 hover:from-teal-300 hover:to-indigo-400 text-[#0f172a] font-bold py-2 rounded-full transition-all">
                    <Ticket size={16} />
                    <span>Comprar Ingresso</span>
                  </button>
                </div>
              )}
            </motion.li>
          ))
        )}
      </ul>
    </div>
  );
}
