// src/LinkPage.js
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function LinkPage({ ticketName, price, role, setStarted, setRole, setTicketName, setPrice }) {
  const [link, setLink] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (ticketName && price && role) {
      const generated = `${window.location.origin}/confirmar?ingresso=${encodeURIComponent(ticketName)}&valor=${encodeURIComponent(price)}&role=${role}`;
      setLink(generated);
    }
  }, [ticketName, price, role]);

  const copyToClipboard = () => {
    navigator.clipboard.writeText(link);
  };

  const handleBack = () => {
    setStarted(false);
  };

  return (
    <section className="flex flex-col items-center text-center px-6 py-16">
      <button onClick={handleBack} className="self-start text-sm text-gray-300 mb-4">← Voltar</button>
      <h2 className="text-2xl font-semibold mb-4">
        Copie esse link e envie para o {role === 'comprador' ? 'vendedor' : 'comprador'}!
      </h2>
      <p className="text-gray-400 mb-2 break-words max-w-xl">{link}</p>
      <button
        className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-xl shadow mb-12"
        onClick={copyToClipboard}
      >
        Copiar link
      </button>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-12 max-w-4xl text-left">
        <div>
          <h3 className="text-xl font-bold mb-2">Como funciona</h3>
          <ul className="list-disc list-inside text-gray-300">
            <li>Vendedor e comprador se conectam por um link da Safeswap</li>
            <li>O comprador envia o pagamento para a Safeswap (via Pix)</li>
            <li>O vendedor envia o ingresso</li>
            <li>O comprador confirma o recebimento no site</li>
            <li>A Safeswap libera o pagamento para o vendedor</li>
          </ul>
        </div>
        <div>
          <h3 className="text-xl font-bold mb-2">Por que usar a Safeswap?</h3>
          <ul className="list-disc list-inside text-gray-300">
            <li>Sem golpes – nós seguramos o dinheiro com segurança</li>
            <li>Transferências rápidas e confiáveis</li>
            <li>Processo simples com menos etapas</li>
            <li>Suporte para qualquer ingresso de evento</li>
            <li>Integrado com Pix</li>
          </ul>
        </div>
      </div>
    </section>
  );
}
