import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const navigate = useNavigate();

  const dateToStr = (date) => date.toISOString().split('T')[0];

  useEffect(() => {
    const ordenadas = festasJson.sort((a, b) => new Date(a.data) - new Date(b.data));
    setFestas(ordenadas);
  }, []);

  const handleDateChange = (date) => {
    setDataSelecionada(date);
    setMostrarCalendario(false);
  };

  const festasFiltradas = dataSelecionada
    ? festas.filter((f) => f.data === dateToStr(dataSelecionada))
    : festas;

  const tileClassName = ({ date }) => {
    const dateStr = dateToStr(date);
    const existeFesta = festas.some((f) => f.data === dateStr);
    return existeFesta ? 'highlight-date' : null;
  };

  return (
    <div className="min-h-screen bg-[#0f172a] text-white font-sans">
      <style>{`
        .react-calendar {
          background-color: #1e293b;
          color: #e0f2f1;
          border-radius: 1rem;
          border: none;
          font-family: 'Inter', sans-serif;
          padding: 1rem;
        }
        .react-calendar__tile {
          padding: 0.75rem 0.5rem;
          border-radius: 0.5rem;
          transition: background 0.2s ease;
        }
        .react-calendar__tile--now {
          background: rgba(61, 218, 215, 0.1);
        }
        .react-calendar__tile--active {
          background: #3ddad7 !important;
          color: #0f172a !important;
        }
        .highlight-date {
          background-color: #3ddad7 !important;
          color: #0f172a !important;
          font-weight: bold;
        }
      `}</style>

      {/* Header */}
      <header className="px-6 py-5 border-b border-[#3ddad7]/20 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-[#3ddad7]">Festas SafeSwap</h1>
        <button
          onClick={() => navigate('/')}
          className="text-sm text-[#58e6dc] hover:underline"
        >
          Voltar ao início
        </button>
      </header>

      {/* Filtro por calendário */}
      <div className="max-w-5xl mx-auto px-6 pt-10">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-lg font-semibold">Filtrar por data</h2>
          <button
            onClick={() => setMostrarCalendario(!mostrarCalendario)}
            className="text-sm text-[#58e6dc] hover:text-[#3ddad7] transition"
          >
            {mostrarCalendario ? 'Fechar calendário' : 'Abrir calendário'}
          </button>
        </div>

        {mostrarCalendario && (
          <div className="mb-8">
            <Calendar
              onChange={handleDateChange}
              value={dataSelecionada}
              tileClassName={tileClassName}
            />
            {dataSelecionada && (
              <button
                onClick={() => setDataSelecionada(null)}
                className="mt-2 text-sm text-[#3ddad7] underline"
              >
                Limpar filtro
              </button>
            )}
          </div>
        )}
      </div>

      {/* Lista de festas */}
      <section className="max-w-5xl mx-auto px-6 pb-20">
        {festasFiltradas.length === 0 ? (
          <div className="text-center text-gray-400 py-20">
            Nenhuma festa encontrada nesta data.
          </div>
        ) : (
          <ul className="grid md:grid-cols-2 gap-8">
            {festasFiltradas.map((festa, index) => (
              <li
                key={index}
                className="bg-[#1e293b] border border-[#3ddad7]/10 rounded-xl overflow-hidden shadow-sm hover:shadow-md transition"
              >
                {festa.imagem && (
                  <img
                    src={festa.imagem}
                    alt={festa.nome}
                    className="w-full h-48 object-cover"
                  />
                )}
                <div className="p-5 space-y-3">
                  <div className="flex justify-between items-center">
                    <h3 className="text-xl font-semibold text-white">{festa.nome}</h3>
                    <span className="text-xs bg-[#3ddad7]/10 text-[#3ddad7] px-3 py-1 rounded-full">
                      {new Date(festa.data).toLocaleDateString('pt-BR')}
                    </span>
                  </div>
                  {festa.descricao && (
                    <p className="text-sm text-gray-300">{festa.descricao}</p>
                  )}
                  {festa.categorias && (
                    <ul className="text-sm text-white space-y-1">
                      {Object.entries(festa.categorias).map(([cat, preco], i) => (
                        <li key={i} className="flex justify-between">
                          <span>{cat}</span>
                          <span className="text-[#58e6dc] font-medium">R$ {preco}</span>
                        </li>
                      ))}
                    </ul>
                  )}
                  <button className="w-full mt-3 bg-[#3ddad7] hover:bg-[#58e6dc] text-[#0f172a] font-semibold py-2 rounded-md transition">
                    Comprar ingresso
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}
