import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

export default function FestasPage() {
  const navigate = useNavigate();
  const [isCalendarExpanded, setIsCalendarExpanded] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [priceRange, setPriceRange] = useState([0, 500]); // Min e Max em reais
  const [festas, setFestas] = useState([]);
  const [filteredFestas, setFilteredFestas] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [showPriceFilter, setShowPriceFilter] = useState(false);

  // Dados de festas do seu festas.json
  useEffect(() => {
    // Simular carregamento de dados do seu festas.json
    const mockFestas = [
      {
        id: 1,
        nome: "Eletrônica Experience",
        data: "2025-05-25",
        local: "Club XYZ, São Paulo",
        endereco: "Rua Augusta, 1500 - Cerqueira César, São Paulo, SP",
        imagem: "/festa1.jpg",
        descricao: "Uma festa de música eletrônica com os melhores DJs do Brasil.",
        categorias: { "Pista": 80, "VIP": 150, "Camarote": 250 }
      },
      {
        id: 2,
        nome: "Festival de Verão",
        data: "2025-06-15",
        local: "Praia Grande, Santos",
        endereco: "Av. Presidente Castelo Branco - Praia Grande, Santos, SP",
        imagem: "/festa2.jpg",
        descricao: "O maior festival de verão do litoral paulista com atrações nacionais.",
        categorias: { "Meia-entrada": 120, "Inteira": 240, "Premium": 400 }
      },
      {
        id: 3,
        nome: "Rock in Rio",
        data: "2025-07-08",
        local: "Cidade do Rock, Rio de Janeiro",
        endereco: "Parque Olímpico, Av. Embaixador Abelardo Bueno - Barra da Tijuca, Rio de Janeiro, RJ",
        imagem: "/festa3.jpg",
        descricao: "O maior festival de música do mundo retorna ao Brasil.",
        categorias: { "Meia-entrada": 200, "Inteira": 400, "Premium": 800 }
      },
      {
        id: 4,
        nome: "Sertanejo Fest",
        data: "2025-05-30",
        local: "Arena Country, Goiânia",
        endereco: "BR-153, km 5,5 - Zona Rural, Goiânia, GO",
        imagem: "/festa4.jpg",
        descricao: "Uma noite com os maiores nomes do sertanejo brasileiro.",
        categorias: { "Comum": 90, "Front-stage": 200, "Camarote": 350 }
      },
      {
        id: 5,
        nome: "Tomorrowland Brasil",
        data: "2025-06-25",
        local: "Parque Maeda, São Paulo",
        endereco: "Rodovia dos Bandeirantes, km 72 - Itu, São Paulo, SP",
        imagem: "/festa5.jpg",
        descricao: "A maior festa de música eletrônica do mundo chega ao Brasil.",
        categorias: { "Day Pass": 300, "Full Pass": 650, "VIP Full Pass": 1200 }
      }
    ];

    // Adicionar propriedades de preço mínimo e máximo para facilitar filtragem
    const festasComPrecos = mockFestas.map(festa => {
      const precos = Object.values(festa.categorias);
      return {
        ...festa,
        preco_min: Math.min(...precos),
        preco_max: Math.max(...precos)
      };
    });

    setFestas(festasComPrecos);
    setFilteredFestas(festasComPrecos);
    
    // Simular carregamento
    setTimeout(() => {
      setIsLoaded(true);
    }, 300);
  }, []);

  // Filtrar festas quando data ou preço mudar
  useEffect(() => {
    let result = [...festas];
    
    // Filtrar por data se selecionada
    if (selectedDate) {
      const dateStr = selectedDate.toISOString().split('T')[0];
      result = result.filter(festa => festa.data === dateStr);
    }
    
    // Filtrar por faixa de preço
    result = result.filter(festa => 
      festa.preco_min <= priceRange[1] && festa.preco_max >= priceRange[0]
    );
    
    setFilteredFestas(result);
  }, [selectedDate, priceRange, festas]);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const clearFilters = () => {
    setSelectedDate(null);
    setPriceRange([0, 500]);
  };

  const handlePriceChange = (e, index) => {
    const newRange = [...priceRange];
    newRange[index] = parseInt(e.target.value);
    setPriceRange(newRange);
  };

  const toggleCalendar = () => {
    setIsCalendarExpanded(!isCalendarExpanded);
  };

  const togglePriceFilter = () => {
    setShowPriceFilter(!showPriceFilter);
  };

  const navigateToFesta = (festa) => {
    navigate(`/festa/${festa.id}`, { state: { festa } });
  };

  return (
    <div className="min-h-screen w-full bg-[#0f172a] text-white">
      {/* Header com título e botões de filtro */}
      <div className="bg-[#0f172a] px-6 py-4 border-b border-[#1e293b] sticky top-0 z-30">
        <div className="flex justify-between items-center max-w-6xl mx-auto">
          <h1 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-teal-400 to-cyan-500">
            Eventos Disponíveis
          </h1>
          
          <div className="flex items-center gap-2">
            <button 
              onClick={toggleCalendar} 
              className="bg-[#1e293b] hover:bg-[#2d3e5a] px-3 py-2 rounded-xl text-sm flex items-center gap-1 transition"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
              {isCalendarExpanded ? 'Ocultar' : 'Mostrar'} Calendário
            </button>
            
            <button 
              onClick={togglePriceFilter}
              className="bg-[#1e293b] hover:bg-[#2d3e5a] px-3 py-2 rounded-xl text-sm flex items-center gap-1 transition"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6V4m0 2a2 2 0 100 4m0-4a2 2 0 110 4m-6 8a2 2 0 100-4m0 4a2 2 0 110-4m0 4v2m0-6V4m6 6v10m6-2a2 2 0 100-4m0 4a2 2 0 110-4m0 4v2m0-6V4" />
              </svg>
              Filtro de Preço
            </button>
            
            {(selectedDate || priceRange[0] > 0 || priceRange[1] < 500) && (
              <button 
                onClick={clearFilters}
                className="bg-red-500/20 hover:bg-red-500/30 text-red-300 px-3 py-2 rounded-xl text-sm flex items-center gap-1 transition"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
                Limpar Filtros
              </button>
            )}
          </div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto px-6 py-6">
        <div className="flex flex-col sm:flex-row gap-6">
          {/* Painel lateral de filtros */}
          <div className="w-full sm:w-64 flex-shrink-0">
            {/* Calendário colapsável */}
            <div className={`transition-all duration-300 ease-in-out overflow-hidden ${isCalendarExpanded ? 'max-h-[26rem] opacity-100' : 'max-h-0 opacity-0'}`}>
              <div className="bg-[#1e293b] rounded-xl p-4 mb-4">
                <div className="flex justify-between items-center mb-3">
                  <h2 className="text-lg font-semibold text-teal-400">Data</h2>
                  {selectedDate && (
                    <button 
                      onClick={() => setSelectedDate(null)}
                      className="text-xs text-gray-400 hover:text-white transition"
                    >
                      Limpar
                    </button>
                  )}
                </div>
                
                <div className="calendar-container">
                  <Calendar 
                    onChange={handleDateChange} 
                    value={selectedDate}
                    className="bg-[#0f172a] border-[#1e293b] rounded-lg w-full"
                  />
                </div>
                
                {selectedDate && (
                  <div className="mt-3 text-sm text-center text-teal-300">
                    Data selecionada: {selectedDate.toLocaleDateString()}
                  </div>
                )}
              </div>
            </div>
            
            {/* Filtro de preço expansível */}
            <div className={`transition-all duration-300 ease-in-out overflow-hidden ${showPriceFilter ? 'max-h-[20rem] opacity-100' : 'max-h-0 opacity-0'}`}>
              <div className="bg-[#1e293b] rounded-xl p-4 mb-4">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-lg font-semibold text-teal-400">Preço</h2>
                  <div className="text-xs text-gray-400">
                    R$ {priceRange[0]} - R$ {priceRange[1]}
                  </div>
                </div>
                
                <div className="mb-6">
                  <label className="text-sm text-gray-300 mb-2 block">Preço Mínimo: R$ {priceRange[0]}</label>
                  <input 
                    type="range" 
                    min="0" 
                    max="500" 
                    step="10"
                    value={priceRange[0]}
                    onChange={(e) => handlePriceChange(e, 0)}
                    className="w-full h-2 bg-[#0f172a] rounded-lg appearance-none cursor-pointer"
                  />
                </div>
                
                <div>
                  <label className="text-sm text-gray-300 mb-2 block">Preço Máximo: R$ {priceRange[1]}</label>
                  <input 
                    type="range" 
                    min="0" 
                    max="1500" 
                    step="50"
                    value={priceRange[1]}
                    onChange={(e) => handlePriceChange(e, 1)}
                    className="w-full h-2 bg-[#0f172a] rounded-lg appearance-none cursor-pointer"
                  />
                </div>
              </div>
            </div>
            
            {/* Status dos filtros ativos */}
            <div className="bg-[#1e293b] rounded-xl p-4">
              <h2 className="text-lg font-semibold text-teal-400 mb-3">Filtros Ativos</h2>
              
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-300">Data:</span>
                  <span className="text-white">{selectedDate ? selectedDate.toLocaleDateString() : 'Todas'}</span>
                </div>
                
                <div className="flex justify-between">
                  <span className="text-gray-300">Preço:</span>
                  <span className="text-white">R$ {priceRange[0]} - R$ {priceRange[1]}</span>
                </div>
                
                <div className="flex justify-between">
                  <span className="text-gray-300">Eventos Encontrados:</span>
                  <span className="text-white">{filteredFestas.length}</span>
                </div>
              </div>
            </div>
            
            {/* Botão para voltar */}
            <button
              onClick={() => navigate('/')}
              className="w-full mt-4 bg-[#1e293b] hover:bg-[#2d3e5a] py-2 rounded-xl text-teal-400 flex items-center justify-center gap-2 transition"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
              </svg>
              Voltar para Home
            </button>
          </div>
          
          {/* Área principal com lista de festas */}
          <div className="flex-grow">
            {/* Estado de carregamento */}
            {!isLoaded ? (
              <div className="flex justify-center items-center h-64">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-teal-400"></div>
              </div>
            ) : filteredFestas.length === 0 ? (
              <div className="bg-[#1e293b] rounded-xl p-8 text-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 text-gray-500 mx-auto mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <h3 className="text-xl font-semibold text-gray-300">Nenhum evento encontrado</h3>
                <p className="text-gray-400 mt-2">Tente ajustar seus filtros para ver mais resultados</p>
                <button 
                  onClick={clearFilters}
                  className="mt-4 bg-teal-500/20 hover:bg-teal-500/30 text-teal-300 px-4 py-2 rounded-lg text-sm transition"
                >
                  Limpar todos os filtros
                </button>
              </div>
            ) : (
              <div className="grid gap-6 grid-cols-1 md:grid-cols-2 lg:grid-cols-3">
                {filteredFestas.map((festa) => (
                  <div 
                    key={festa.id} 
                    className="bg-[#1e293b] rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition cursor-pointer"
                    onClick={() => navigateToFesta(festa)}
                  >
                    <div className="h-48 overflow-hidden relative">
                      <div className="absolute inset-0 bg-gradient-to-t from-[#0f172a] to-transparent opacity-60 z-10"></div>
                      <img 
                        src={festa.imagem || "https://via.placeholder.com/400x200?text=Evento"} 
                        alt={festa.nome} 
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute bottom-0 left-0 right-0 p-4 z-20">
                        <div className="flex justify-between items-center">
                          <div className="bg-black/60 backdrop-blur-sm px-3 py-1 rounded-full text-xs">
                            {new Date(festa.data).toLocaleDateString()}
                          </div>
                          <div className="bg-teal-500/20 backdrop-blur-sm px-3 py-1 rounded-full text-xs text-teal-300 font-medium">
                            R$ {festa.preco_min} - R$ {festa.preco_max}
                          </div>
                        </div>
                      </div>
                    </div>
                    
                    <div className="p-4">
                      <h3 className="text-lg font-bold mb-2">{festa.nome}</h3>
                      
                      <div className="flex items-center text-sm text-gray-300 mb-4">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1 text-teal-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                        </svg>
                        <span>{festa.local}</span>
                      </div>
                      
                      <button className="w-full bg-gradient-to-r from-teal-500 to-cyan-500 text-white py-2 rounded-lg text-sm font-medium hover:from-teal-400 hover:to-cyan-400 transition">
                        Ver Detalhes
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
      
      {/* Notificação de resultados filtrados */}
      {(selectedDate || priceRange[0] > 0 || priceRange[1] < 500) && filteredFestas.length > 0 && (
        <div className="fixed bottom-6 right-6 z-50 bg-teal-500/90 backdrop-blur-sm px-4 py-2 rounded-lg shadow-lg transition-all duration-300 flex items-center gap-2">
          <span className="text-sm font-medium">Exibindo {filteredFestas.length} eventos</span>
          <button 
            onClick={clearFilters}
            className="text-xs bg-white/20 hover:bg-white/30 px-2 py-1 rounded transition"
          >
            Limpar
          </button>
        </div>
      )}
      
      {/* Estilos personalizados para o calendário e sliders */}
      <style jsx>{`
        /* Calendário */
        .react-calendar {
          background-color: #0f172a;
          border-color: #1e293b;
          border-radius: 0.5rem;
          color: white;
