import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import festasJson from './festas.json';

export default function FestasPage() {
  const [festas, setFestas] = useState([]);
  const [dataSelecionada, setDataSelecionada] = useState(null);
  const [festaAberta, setFestaAberta] = useState(null);
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const navigate = useNavigate();

  const dateToStr = (date) => date.toISOString().split('T')[0];

  useEffect(() => {
    const ordenadas = festasJson.sort((a, b) => new Date(a.data) - new Date(b.data));
    setFestas(ordenadas);
  }, []);

  const handleDateChange = (date) => {
    setDataSelecionada(date);
    setFestaAberta(null);
    setMostrarCalendario(false);
  };

  const festasFiltradas = dataSelecionada
    ? festas.filter((f) => f.data === dateToStr(dataSelecionada))
    : festas;

  const tileClassName = ({ date }) => {
    const dateStr = dateToStr(date);
    const existeFesta = festas.some((f) => f.data === dateStr);
    return existeFesta ? 'highlight-date' : null;
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-900 to-slate-800 text-white">
      <style>
        {`
          .react-calendar {
            background-color: #1e293b;
            color: #e0f2f1;
            border-radius: 1rem;
            border: none;
            font-family: 'Inter', sans-serif;
            padding: 1rem;
            margin-top: 1rem;
          }
          .react-calendar__tile {
            padding: 0.75rem 0.5rem;
            border-radius: 0.5rem;
            transition: background 0.2s ease;
            color: #e0f2f1;
          }
          .react-calendar__tile--now {
            background: rgba(61, 218, 215, 0.1);
          }
          .react-calendar__tile--active {
            background: #3ddad7 !important;
            color: #0f172a !important;
          }
          .react-calendar__tile:hover {
            background: rgba(61, 218, 215, 0.15);
          }
          .react-calendar__month-view__weekdays {
            text-transform: uppercase;
            font-size: 0.75rem;
            color: #94a3b8;
          }
          .react-calendar__navigation button {
            color: #58e6dc;
            background: none;
            font-weight: bold;
          }
          .highlight-date {
            background-color: #3ddad7 !important;
            color: #0f172a !important;
            font-weight: bold;
            border-radius: 0.5rem;
          }
        `}
      </style>

      {/* Banner com gradiente e título */}
      <div className="relative pb-12">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-600 to-teal-500 opacity-80"></div>
        <div className="relative z-10 pt-16 pb-10 text-center px-4">
          <button
            onClick={() => navigate('/')}
            className="absolute top-4 left-4 text-white hover:text-gray-200 transition flex items-center bg-black/20 rounded-lg px-3 py-2"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
            <span className="text-sm font-medium">Voltar</span>
          </button>
          <h1 className="text-5xl font-extrabold text-white">Festas Disponíveis</h1>
          <p className="text-white/80 mt-4 font-medium max-w-md mx-auto">
            Clique no nome da festa para ver mais detalhes e garantir seu ingresso.
          </p>
          <div className="mt-6">
            <button
              onClick={() => setMostrarCalendario(!mostrarCalendario)}
              className="bg-[#1e293b] hover:bg-[#273549] text-[#58e6dc] px-4 py-2 rounded-lg font-medium transition"
            >
              {mostrarCalendario ? 'Esconder calendário' : 'Abrir calendário'}
            </button>
            {mostrarCalendario && (
              <div className="mt-4 max-w-sm mx-auto">
                <Calendar
                  onChange={handleDateChange}
                  value={dataSelecionada}
                  tileClassName={tileClassName}
                />
                {dataSelecionada && (
                  <button
                    onClick={() => setDataSelecionada(null)}
                    className="mt-2 text-sm text-[#3ddad7] underline"
                  >
                    Ver todas as festas
                  </button>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Lista de festas */}
      <div className="max-w-3xl mx-auto px-4">
        {festasFiltradas.length === 0 ? (
          <div className="text-center text-gray-400 text-lg py-20 bg-slate-800/70 rounded-2xl shadow-lg">
            Nenhuma festa disponível nesta data.
          </div>
        ) : (
          <ul className="space-y-6">
            {festasFiltradas.map((festa, index) => (
              <li
                key={index}
                className="bg-slate-800/80 p-6 rounded-2xl shadow-md border border-cyan-500/20 hover:border-cyan-400/40 transition"
              >
                <button
                  onClick={() =>
                    setFestaAberta(festaAberta === index ? null : index)
                  }
                  className="flex justify-between items-center w-full text-left"
                >
                  <h3 className="text-xl font-bold text-cyan-300">{festa.nome}</h3>
                  <span className="text-sm bg-cyan-500/10 text-cyan-300 py-1 px-3 rounded-full">
                    {new Date(festa.data).toLocaleDateString('pt-BR')}
                  </span>
                </button>

                {festaAberta === index && (
                  <div className="mt-4 space-y-4">
                    {festa.imagem && (
                      <img
                        src={festa.imagem}
                        alt={festa.nome}
                        className="w-full h-56 object-cover rounded-lg shadow"
                      />
                    )}
                    {festa.descricao && (
                      <p className="text-gray-300 text-sm">{festa.descricao}</p>
                    )}
                    {festa.categorias && (
                      <div className="bg-slate-900/40 rounded-lg p-4">
                        <h4 className="text-cyan-400 font-semibold mb-2">
                          Categorias de Ingressos
                        </h4>
                        <ul className="divide-y divide-slate-700/60">
                          {Object.entries(festa.categorias).map(([cat, preco], i) => (
                            <li key={i} className="flex justify-between py-2">
                              <span className="text-white">{cat}</span>
                              <span className="text-cyan-300 font-bold">R$ {preco}</span>
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                    <button className="w-full bg-gradient-to-r from-cyan-500 to-teal-400 hover:from-cyan-600 hover:to-teal-500 text-white font-bold py-3 rounded-lg transition">
                      Comprar Ingresso
                    </button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
