// src/App.js
import { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import LinkPage from './LinkPage';
import ConfirmPage from './ConfirmPage';

export default function App() {
  const [role, setRole] = useState(null);
  const [started, setStarted] = useState(false);
  const [ticketName, setTicketName] = useState('');
  const [price, setPrice] = useState('');
  const navigate = useNavigate();

  const handleBack = () => {
    if (started) {
      setStarted(false);
    } else if (role) {
      setRole(null);
    }
  };

  return (
    <main className="min-h-screen bg-gray-900 text-white font-sans">
      <header className="w-full p-6 shadow-md bg-gray-950 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <img src="/logo.png.jpg" alt="logo" className="h-10 w-10 rounded-xl" />
          <h1 className="text-2xl font-bold tracking-tight">Safeswap</h1>
        </div>
        <p className="text-sm text-gray-400">Seguro e rápido</p>
      </header>

      <section className="flex flex-col items-center justify-center text-center px-6 py-16">
        <Routes>
          <Route
            path="/"
            element={
              role && started ? (
                <>
                  <button onClick={handleBack} className="self-start text-left text-sm text-gray-300 mb-4">← Voltar</button>
                  <h2 className="text-2xl font-semibold mb-4">Preencha as informações do ingresso</h2>
                  <input
                    placeholder="Nome do ingresso"
                    className="mb-4 px-4 py-2 rounded text-black"
                    value={ticketName}
                    onChange={(e) => setTicketName(e.target.value)}
                  />
                  <input
                    placeholder="Preço combinado (R$)"
                    className="mb-4 px-4 py-2 rounded text-black"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                  />
                  <button
                    className="bg-white text-gray-900 font-semibold px-6 py-2 rounded-xl shadow"
                    onClick={() => navigate('/link')}
                  >
                    Gerar link
                  </button>
                </>
              ) : !role ? (
                <>
                  <h2 className="text-4xl font-bold mb-6">Intermediação para Venda Segura de Ingressos</h2>
                  <p className="text-lg text-gray-300 max-w-xl mb-8">
                    Compradores e vendedores podem usar um link único para trocar ingressos com segurança.
                    O dinheiro é retido até o comprador confirmar o recebimento.
                  </p>
                  <div className="flex gap-4">
                    <button onClick={() => setRole('comprador')} className="bg-blue-600 px-6 py-2 rounded-xl">Sou Comprador</button>
                    <button onClick={() => setRole('vendedor')} className="bg-green-600 px-6 py-2 rounded-xl">Sou Vendedor</button>
                  </div>
                </>
              ) : null
            }
          />

          <Route
            path="/link"
            element={
              <LinkPage
                ticketName={ticketName}
                price={price}
                role={role}
                setStarted={setStarted}
                setRole={setRole}
                setTicketName={setTicketName}
                setPrice={setPrice}
              />
            }
          />

          <Route path="/confirmar" element={<ConfirmPage />} />

          <Route
            path="/pagamento"
            element={
              <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center text-center">
                <h1 className="text-3xl font-bold mb-2">Pagamento</h1>
                <p className="text-gray-400">Agora a outra parte deve transferir o valor por Pix.</p>
              </div>
            }
          />

          <Route
            path="/cancelado"
            element={
              <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center text-center">
                <h1 className="text-3xl font-bold mb-2">Operação cancelada</h1>
                <p className="text-gray-400">O outro usuário recusou a transação.</p>
              </div>
            }
          />
        </Routes>
      </section>
    </main>
  );
}
