import { useState } from 'react';

export default function Safeswap() {
  const [role, setRole] = useState(null); // comprador ou vendedor
  const [started, setStarted] = useState(false); // se clicou em "começar"
  const [ticketName, setTicketName] = useState('');
  const [price, setPrice] = useState('');

  return (
    <main className="min-h-screen bg-gray-900 text-white font-sans">
      <header className="w-full p-6 shadow-md bg-gray-950">
        <div className="max-w-5xl mx-auto flex justify-between items-center">
          <div className="flex items-center gap-3">
            <img src="/logo.png" alt="Logo Safeswap" className="w-10 h-10" />
            <h1 className="text-2xl font-bold tracking-tight">Safeswap</h1>
          </div>
          <p className="text-sm text-gray-400">Seguro e rápido</p>
        </div>
      </header>

      <section className="flex flex-col items-center justify-center text-center px-6 py-20">
        <h2 className="text-4xl font-bold mb-4">Intermediação para Vendas de Ingressos Seguras</h2>
        <p className="text-lg text-gray-300 max-w-xl mb-6">
          Compradores e vendedores podem usar um único link para trocar ingressos com segurança.
          Nós seguramos o pagamento até que o comprador confirme o recebimento, garantindo segurança para ambos.
        </p>

        {/* Etapa 1 - Escolha de função */}
        {!role && !started && (
          <div className="flex gap-4">
            <button
              className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-xl shadow-md"
              onClick={() => setRole('comprador')}
            >
              Sou Comprador
            </button>
            <button
              className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-xl shadow-md"
              onClick={() => setRole('vendedor')}
            >
              Sou Vendedor
            </button>
          </div>
        )}

        {/* Etapa 2 - Botão "Começar" */}
        {role && !started && (
          <div className="flex flex-col items-center gap-4 mt-6">
            <p className="text-gray-400 text-sm">Selecionado: <strong>{role}</strong></p>
            <button
              className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-2xl shadow-md"
              onClick={() => setStarted(true)}
            >
              Começar
            </button>
          </div>
        )}

        {/* Etapa 3 - Inputs do ingresso */}
        {started && (
          <div className="flex flex-col items-center gap-6 mt-6 w-full max-w-md">
            <p className="text-gray-300 text-lg">
              Você está como <strong>{role}</strong>. Preencha os dados da transação:
            </p>

            <input
              type="text"
              placeholder="Nome do ingresso (ex: Lollapalooza)"
              className="w-full p-3 rounded-lg text-black"
              value={ticketName}
              onChange={(e) => setTicketName(e.target.value)}
            />

            <input
              type="number"
              placeholder="Preço combinado (R$)"
              className="w-full p-3 rounded-lg text-black"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />

            <button
              className="bg-white text-gray-900 font-semibold px-6 py-3 rounded-xl shadow-md"
              onClick={() => {
                alert(`Função: ${role}\nIngresso: ${ticketName}\nPreço: R$ ${price}`);
                // Aqui você poderia redirecionar, salvar em banco ou gerar link
              }}
            >
              Confirmar
            </button>
          </div>
        )}
      </section>
    </main>
  );
}
