// src/FestaDetailPage.js
import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

export default function FestaDetailPage() {
  const navigate = useNavigate();
  const { state: { festa } = {} } = useLocation();

  if (!festa) {
    navigate('/', { replace: true });
    return null;
  }

  const displayDate = (iso) => {
    const [year, month, day] = iso.split('-');
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-[#0f172a] to-[#1e293b] px-12 py-16 text-white font-sans">
      {/* Botão de Voltar */}
      <button
        onClick={() => navigate(-1)}
        className="text-lg text-gray-300 hover:text-white mb-8"
      >
        ← Voltar
      </button>

      {/* Conteúdo principal ocupa toda a largura */}
      <div className="flex flex-col lg:flex-row w-full bg-slate-800 bg-opacity-60 backdrop-blur-lg rounded-2xl overflow-hidden shadow-2xl">
        {/* Imagem do evento */}
        {festa.imagem && (
          <div className="w-full lg:w-1/2 h-80 lg:h-auto">
            <img
              src={festa.imagem}
              alt={festa.nome}
              className="w-full h-full object-cover"
            />
          </div>
        )}

        {/* Detalhes da festa */}
        <div className="w-full lg:w-1/2 p-12 space-y-8">
          <h1 className="text-4xl lg:text-5xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-teal-300">
            {festa.nome}
          </h1>

          <div className="flex flex-col sm:flex-row gap-6 text-gray-300 text-lg">
            <div className="flex items-center space-x-3">
              <span>📅</span>
              <span>{displayDate(festa.data)}</span>
            </div>
            <div className="flex items-center space-x-3">
              <span>📍</span>
              <span>{festa.local}</span>
            </div>
          </div>

          <p className="text-gray-200 leading-relaxed text-lg">
            {festa.descricao}
          </p>

          <div>
            <h2 className="text-2xl font-semibold mb-4">Categorias de Ingressos</h2>
            <ul className="space-y-4">
              {Object.entries(festa.categorias).map(([cat, preco]) => (
                <li
                  key={cat}
                  className="flex justify-between items-center bg-slate-700 bg-opacity-40 rounded-lg px-6 py-3"
                >
                  <span className="font-medium text-lg">{cat}</span>
                  <span className="font-bold text-lg">R$ {preco}</span>
                </li>
              ))}
            </ul>
          </div>

          <button
            onClick={() => navigate('/checkout', { state: { festa } })}
            className="w-full mt-6 py-4 bg-gradient-to-r from-indigo-500 to-teal-400 text-gray-900 font-bold text-xl rounded-full shadow-xl hover:scale-105 transform transition"
          >
            🎫 Comprar Ingresso
          </button>
        </div>
      </div>
    </div>
  );
}
