// src/LinkPage.js
import React from 'react';

export default function LinkPage({ ticketName, price, role, setStarted }) {
  if (!ticketName || !price || !role) {
    return (
      <div className="text-white p-10 text-center">
        <p>Erro ao carregar formulário.</p>
      </div>
    );
  }

  const opposite = role === 'comprador' ? 'vendedor' : 'comprador';
  const url = `${window.location.origin}/confirmar?ingresso=${encodeURIComponent(ticketName)}&valor=${encodeURIComponent(price)}&role=${role}`;

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(url);
      alert('Link copiado!');
    } catch (err) {
      alert('Erro ao copiar o link');
    }
  };

  return (
    <div className="text-white px-4 py-12 text-center flex flex-col items-center gap-6">
      <button
        onClick={() => setStarted(false)}
        className="text-sm text-white self-start hover:underline"
      >
        ← Voltar
      </button>

      <h2 className="text-2xl font-semibold">
        Copie esse link e envie para o {opposite}!
      </h2>

      <p className="text-gray-300 break-all max-w-xl">{url}</p>

      <button
        onClick={copyToClipboard}
        className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg shadow"
      >
        Copiar link
      </button>

      <div className="mt-12 flex flex-col md:flex-row justify-center gap-12 text-left">
        <div>
          <h3 className="text-xl font-bold mb-2">Como funciona</h3>
          <ul className="list-disc ml-5 text-gray-300">
            <li>Vendedor e comprador se conectam por um link da Safeswap</li>
            <li>O comprador envia o pagamento para a Safeswap (via Pix)</li>
            <li>O vendedor envia o ingresso</li>
            <li>O comprador confirma o recebimento no site</li>
            <li>A Safeswap libera o pagamento para o vendedor</li>
          </ul>
        </div>

        <div>
          <h3 className="text-xl font-bold mb-2">Por que usar a Safeswap?</h3>
          <ul className="list-disc ml-5 text-gray-300">
            <li>Sem golpes – nós seguramos o dinheiro com segurança</li>
            <li>Transferências rápidas e confiáveis</li>
            <li>Processo simples com menos etapas</li>
            <li>Suporte para qualquer ingresso de evento</li>
            <li>Integrado com Pix</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
